00376 /*
00377  * Pulse parms are ((X*50)-100) for the Mark and ((X*50)+100) for the Space.
00378  * First MARK is the one after the long gap
00379  * Pulse parameters in uSec
00380  */
00382 #define TOLERANCE       25
00383 
00385 //#define LTOL            (1.0 - (TOLERANCE/100.))
00386 #define LTOL            (100 - TOLERANCE)
00388 //#define UTOL            (1.0 + (TOLERANCE/100.))
00389 #define UTOL            (100 + TOLERANCE)
00390 
00391 //#define TICKS_LOW(us)   ((int)(((us)*LTOL/MICROS_PER_TICK)))
00392 //#define TICKS_HIGH(us)  ((int)(((us)*UTOL/MICROS_PER_TICK + 1)))
00393 #if MICROS_PER_TICK == 50 && TOLERANCE == 25           // Defaults
00394 #define TICKS_LOW(us)   ((us)/67 )     // (us) / ((MICROS_PER_TICK:50 / LTOL:75 ) * 100)
00395 #define TICKS_HIGH(us)  ((us)/40 + 1)  // (us) / ((MICROS_PER_TICK:50 / UTOL:125) * 100) + 1
00396 #else
00397     #define TICKS_LOW(us)   ((uint16_t) ((long) (us) * LTOL / (MICROS_PER_TICK * 100) ))
00398     #define TICKS_HIGH(us)  ((uint16_t) ((long) (us) * UTOL / (MICROS_PER_TICK * 100) + 1))
00399 #endif
