00105 #if defined(ESP8266) || defined(ESP32)
00106 void IRAM_ATTR IRPinChangeInterruptHandler(void)
00107 #else
00108 void IRPinChangeInterruptHandler(void)
00109 #endif
00110 {
00111 #if defined(_IR_MEASURE_TIMING) && defined(_IR_TIMING_TEST_PIN)
00112     digitalWriteFast(_IR_TIMING_TEST_PIN, HIGH); // 2 clock cycles
00113 #endif
00114     /*
00115      * Save IR input level
00116      * Negative logic, true / HIGH means inactive / IR space, LOW / false means IR mark.
00117      */
00118     uint_fast8_t tIRLevel = digitalReadFast(IR_INPUT_PIN);
00119 
00120 #if !defined(NO_LED_FEEDBACK_CODE) && defined(IR_FEEDBACK_LED_PIN)
00121     digitalWriteFast(IR_FEEDBACK_LED_PIN, !tIRLevel);
00122 #endif
00123 
00124     /*
00125      * 1. compute microseconds after last change
00126      */
00127     uint32_t tCurrentMicros = micros();
00128     uint16_t tMicrosOfMarkOrSpace = tCurrentMicros - TinyIRReceiverControl.LastChangeMicros;
00129     TinyIRReceiverControl.LastChangeMicros = tCurrentMicros;
00130 
00131     uint8_t tState = TinyIRReceiverControl.IRReceiverState;
00132 
00133 #ifdef TRACE
00134     Serial.print(tState);
00135     Serial.print(' ');
00136 //    Serial.print(F(" I="));
00137 //    Serial.print(tIRLevel);
00138 //    Serial.print(F(" D="));
00139 //    Serial.print(tDeltaMicros);
00140 //    Serial.println();
00141 #endif
00142 
00143     if (tIRLevel == LOW)
00144     {
00145         /*
00146          * We have a mark here
00147          */
00148         if (tMicrosOfMarkOrSpace > 2 * NEC_HEADER_MARK)
00149         {
00150             // timeout -> must reset state machine
00151             tState = IR_RECEIVER_STATE_WAITING_FOR_START_MARK;
00152         }
00153         if (tState == IR_RECEIVER_STATE_WAITING_FOR_START_MARK)
00154         {
00155             // We are at the beginning of the header mark, check timing at the next transition
00156             tState = IR_RECEIVER_STATE_WAITING_FOR_START_SPACE;
00157         }
00158 
00159         else if (tState == IR_RECEIVER_STATE_WAITING_FOR_FIRST_DATA_MARK)
00160         {
00161             if (tMicrosOfMarkOrSpace >= lowerValue25Percent(NEC_HEADER_SPACE)
00162                     && tMicrosOfMarkOrSpace <= upperValue25Percent(NEC_HEADER_SPACE))
00163             {
00164                 /*
00165                  * We have a valid data header space here -> initialize data
00166                  */
00167                 TinyIRReceiverControl.IRRawDataBitCounter = 0;
00168                 TinyIRReceiverControl.IRRawData.ULong = 0;
00169                 TinyIRReceiverControl.IRRawDataMask = 1;
00170                 TinyIRReceiverControl.IRRepeatDetected = false;
00171                 tState = IR_RECEIVER_STATE_WAITING_FOR_DATA_SPACE;
00172             }
00173             else if (tMicrosOfMarkOrSpace >= lowerValue25Percent(NEC_REPEAT_HEADER_SPACE)
00174                     && tMicrosOfMarkOrSpace <= upperValue25Percent(NEC_REPEAT_HEADER_SPACE)
00175                     && TinyIRReceiverControl.IRRawDataBitCounter >= NEC_BITS)
00176             {
00177                 /*
00178                  * We have a repeat header here and no broken receive before -> set repeat flag
00179                  */
00180                 TinyIRReceiverControl.IRRepeatDetected = true;
00181                 tState = IR_RECEIVER_STATE_WAITING_FOR_DATA_SPACE;
00182             }
00183             else
00184             {
00185                 // This parts are optimized by the compiler into jumps to one code :-)
00186                 // Wrong length -> reset state
00187                 tState = IR_RECEIVER_STATE_WAITING_FOR_START_MARK;
00188             }
00189         }
00190 
00191         else if (tState == IR_RECEIVER_STATE_WAITING_FOR_DATA_MARK)
00192         {
00193             // Check data space length
00194             if (tMicrosOfMarkOrSpace >= lowerValue(NEC_ZERO_SPACE) && tMicrosOfMarkOrSpace <= upperValue(NEC_ONE_SPACE))
00195             {
00196                 // We have a valid bit here
00197                 tState = IR_RECEIVER_STATE_WAITING_FOR_DATA_SPACE;
00198                 if (tMicrosOfMarkOrSpace >= 2 * NEC_UNIT)
00199                 {
00200                     // we received a 1
00201                     TinyIRReceiverControl.IRRawData.ULong |= TinyIRReceiverControl.IRRawDataMask;
00202                 }
00203                 else
00204                 {
00205                     // we received a 0 - empty code for documentation
00206                 }
00207                 // prepare for next bit
00208                 TinyIRReceiverControl.IRRawDataMask = TinyIRReceiverControl.IRRawDataMask << 1;
00209                 TinyIRReceiverControl.IRRawDataBitCounter++;
00210             }
00211             else
00212             {
00213                 // Wrong length -> reset state
00214                 tState = IR_RECEIVER_STATE_WAITING_FOR_START_MARK;
00215             }
00216         }
00217         else
00218         {
00219             // error wrong state for the received level, e.g. if we missed one change interrupt -> reset state
00220             tState = IR_RECEIVER_STATE_WAITING_FOR_START_MARK;
00221         }
00222     }
00223 
00224     else
00225     {
00226         /*
00227          * We have a space here
00228          */
00229         if (tState == IR_RECEIVER_STATE_WAITING_FOR_START_SPACE)
00230         {
00231             /*
00232              * Check length of header mark here
00233              */
00234             if (tMicrosOfMarkOrSpace >= lowerValue25Percent(NEC_HEADER_MARK)
00235                     && tMicrosOfMarkOrSpace <= upperValue25Percent(NEC_HEADER_MARK))
00236             {
00237                 tState = IR_RECEIVER_STATE_WAITING_FOR_FIRST_DATA_MARK;
00238             }
00239             else
00240             {
00241                 // Wrong length of header mark -> reset state
00242                 tState = IR_RECEIVER_STATE_WAITING_FOR_START_MARK;
00243             }
00244         }
00245 
00246         else if (tState == IR_RECEIVER_STATE_WAITING_FOR_DATA_SPACE)
00247         {
00248             // Check data mark length
00249             if (tMicrosOfMarkOrSpace >= lowerValue(NEC_BIT_MARK) && tMicrosOfMarkOrSpace <= upperValue(NEC_BIT_MARK))
00250             {
00251                 /*
00252                  * We have a valid mark here, check for transmission complete
00253                  */
00254                 if (TinyIRReceiverControl.IRRawDataBitCounter >= NEC_BITS || TinyIRReceiverControl.IRRepeatDetected)
00255                 {
00256                     /*
00257                      * Code complete -> call callback, no parity check!
00258                      */
00259                     // Reset state for new start
00260                     tState = IR_RECEIVER_STATE_WAITING_FOR_START_MARK;
00261 #if !defined(ARDUINO_ARCH_MBED)
00262                     interrupts();
00263 #endif
00264                     /*
00265                      * Address reduction to 8 bit
00266                      */
00267                     if (TinyIRReceiverControl.IRRawData.UByte.LowByte
00268                             == (uint8_t) (~TinyIRReceiverControl.IRRawData.UByte.MidLowByte))
00269                     {
00270                         // standard 8 bit address NEC protocol
00271                         TinyIRReceiverControl.IRRawData.UByte.MidLowByte = 0; // Address is the first 8 bit
00272                     }
00273 
00274                     /*
00275                      * Call user provided callback here
00276                      */
00277                     handleReceivedTinyIRData(TinyIRReceiverControl.IRRawData.UWord.LowWord,
00278                             TinyIRReceiverControl.IRRawData.UByte.MidHighByte, TinyIRReceiverControl.IRRepeatDetected);
00279 
00280                 }
00281                 else
00282                 {
00283                     // not finished yet
00284                     tState = IR_RECEIVER_STATE_WAITING_FOR_DATA_MARK;
00285                 }
00286             }
00287             else
00288             {
00289                 // Wrong length -> reset state
00290                 tState = IR_RECEIVER_STATE_WAITING_FOR_START_MARK;
00291             }
00292         }
00293         else
00294         {
00295             // error wrong state for the received level, e.g. if we missed one change interrupt -> reset state
00296             tState = IR_RECEIVER_STATE_WAITING_FOR_START_MARK;
00297         }
00298     }
00299 
00300     TinyIRReceiverControl.IRReceiverState = tState;
00301 #ifdef _IR_MEASURE_TIMING
00302     digitalWriteFast(_IR_TIMING_TEST_PIN, LOW); // 2 clock cycles
00303 #endif
00304 }
