00514 /*
00515  * Specify the right INT0, INT1 or PCINT0 interrupt vector according to different pins and cores.
00516  * The default value of TINY_RECEIVER_USE_ARDUINO_ATTACH_INTERRUPT is set in TinyIRReceiver.h
00517  */
00518 #if defined(__AVR__) && !defined(TINY_RECEIVER_USE_ARDUINO_ATTACH_INTERRUPT)
00519 #  if (IR_INPUT_PIN == 2)
00520 ISR(INT0_vect) // Pin 2 global assignment
00521 
00522 #  elif (IR_INPUT_PIN == 3)
00523 #    if  defined(ARDUINO_AVR_DIGISPARKPRO)
00524 ISR(INT0_vect) //  Pin 3 / PB6 / INT0 is connected to USB+ on DigisparkPro boards
00525 #    else
00526 ISR(INT1_vect) // Pin 3 global assignment
00527 #    endif
00528 
00529 #  elif (IR_INPUT_PIN == 9) && defined(ARDUINO_AVR_DIGISPARKPRO) // Digispark pro
00530 ISR(INT1_vect)
00531 
00532 #  elif (IR_INPUT_PIN == 14) && (defined(__AVR_ATtiny87__) || defined(__AVR_ATtiny167__))// For AVR_ATtiny167 INT0 is on pin 14 / PB6
00533 ISR(INT0_vect)
00534 
00535 #  elif (! defined(ISC10)) || ((defined(__AVR_ATtiny87__) || defined(__AVR_ATtiny167__)) && INT1_PIN != 3)
00536 // on ATtinyX5 we do not have a INT1_vect but we can use the PCINT0_vect
