00064 void printDurations(uint8_t aArray[], uint8_t aMaxIndex) {
00065     for (uint_fast8_t i = 0; i <= aMaxIndex; i++) {
00066         if (i % 10 == 0) {
00067             if (i == 0) {
00068                 Serial.print(' '); // indentation for the 0
00069             } else {
00070                 Serial.println();
00071             }
00072             Serial.print(i);
00073             Serial.print(F(":"));
00074         }
00075         Serial.print(F(" | "));
00076         Serial.print(aArray[i]);
00077     }
00078     Serial.println();
00079 }
