00082 bool aggregateArrayCounts(uint8_t aArray[], uint8_t aMaxIndex, uint8_t *aShortIndex, uint8_t *aLongIndex) {
00083     uint8_t tSum = 0;
00084     uint16_t tWeightedSum = 0;
00085     for (uint_fast8_t i = 0; i <= aMaxIndex; i++) {
00086         uint8_t tCurrentDurations = aArray[i];
00087         if (tCurrentDurations != 0) {
00088             // Add it to sum and remove array content
00089             tSum += tCurrentDurations;
00090             tWeightedSum += (tCurrentDurations * i);
00091             aArray[i] = 0;
00092         }
00093         if ((tCurrentDurations == 0 || i == aMaxIndex) && tSum != 0) {
00094             // here we have a sum and a gap after the values
00095             uint8_t tAggregateIndex = (tWeightedSum + (tSum / 2)) / tSum; // with rounding
00096             aArray[tAggregateIndex] = tSum; // disabling this line increases code size by 2 - unbelievable!
00097             // store aggregate for later decoding
00098             if (*aShortIndex == 0) {
00099                 *aShortIndex = tAggregateIndex;
00100             } else if (*aLongIndex == 0) {
00101                 *aLongIndex = tAggregateIndex;
00102             } else {
00103                 // we have 3 bins => this is likely no pulse width or distance protocol. e.g. it can be RC5.
00104                 return false;
00105             }
00106             // initialize for next aggregation
00107             tSum = 0;
00108             tWeightedSum = 0;
00109         }
00110     }
00111     return true;
00112 }
