00140     // Count space durations. Skip leading start and trailing stop bit.
00141     for (i = 4; i < (uint_fast8_t) decodedIRData.rawDataPtr->rawlen - 2; i += 2) {
00142         uint8_t tDurationTicks = decodedIRData.rawDataPtr->rawbuf[i];
00143         if (tDurationTicks < sizeof(tDurationArray)) {
00144             tDurationArray[tDurationTicks]++;
00145             if (tMaxDurationIndex < tDurationTicks) {
00146                 tMaxDurationIndex = tDurationTicks;
00147             }
