00043 //==============================================================================
00044 //                             JJJJJ  V   V   CCCC
00045 //                               J    V   V  C
00046 //                               J     V V   C
00047 //                             J J     V V   C
00048 //                              J       V     CCCC
00049 //==============================================================================
00050 // https://www.sbprojects.net/knowledge/ir/jvc.php
00051 // http://www.hifi-remote.com/johnsfine/DecodeIR.html#JVC
00052 // IRP: {38k,525}<1,-1|1,-3>(16,-8,(D:8,F:8,1,-45)+)
00053 // LSB first, 1 start bit + 8 bit address + 8 bit command + 1 stop bit.
00054 // The JVC protocol repeats by skipping the header mark and space -> this leads to a poor repeat detection for JVC protocol.
00055 #define JVC_ADDRESS_BITS      8 // 8 bit address
00056 #define JVC_COMMAND_BITS      8 // Command
00057 
00058 #define JVC_BITS              (JVC_ADDRESS_BITS + JVC_COMMAND_BITS) // 16 - The number of bits in the protocol
00059 #define JVC_UNIT              526 // 20 periods of 38 kHz (526.315789)
00060 
00061 #define JVC_HEADER_MARK       (16 * JVC_UNIT) // 8400
00062 #define JVC_HEADER_SPACE      (8 * JVC_UNIT)  // 4200
00063 
00064 #define JVC_BIT_MARK          JVC_UNIT        // The length of a Bit:Mark
00065 #define JVC_ONE_SPACE         (3 * JVC_UNIT)  // 1578 - The length of a Bit:Space for 1's
00066 #define JVC_ZERO_SPACE        JVC_UNIT        // The length of a Bit:Space for 0's
00067 
00068 #define JVC_REPEAT_SPACE      (uint16_t)(45 * JVC_UNIT)  // 23625 - Commands are repeated with a distance of 23 ms for as long as the key on the remote control is held down.
00069 
00070 //+=============================================================================
00071 // JVC does NOT repeat by sending a separate code (like NEC does).
00072 // The JVC protocol repeats by skipping the header.
00073 //
00074 
00075 void IRsend::sendJVC(uint8_t aAddress, uint8_t aCommand, uint_fast8_t aNumberOfRepeats) {
00076     // Set IR carrier frequency
00077     enableIROut(JVC_KHZ); // 38 kHz
00078 
00079     // Header
00080     mark(JVC_HEADER_MARK);
00081     space(JVC_HEADER_SPACE);
00082 
00083     uint_fast8_t tNumberOfCommands = aNumberOfRepeats + 1;
00084     while (tNumberOfCommands > 0) {
00085 
00086         // Address + command
00087         sendPulseDistanceWidthData(JVC_BIT_MARK, JVC_ONE_SPACE, JVC_BIT_MARK, JVC_ZERO_SPACE,
00088                 aAddress | (aCommand << JVC_ADDRESS_BITS), JVC_BITS, PROTOCOL_IS_LSB_FIRST, SEND_STOP_BIT);
00089 
00090         tNumberOfCommands--;
00091         // skip last delay!
00092         if (tNumberOfCommands > 0) {
00093             // send repeated command in a fixed raster
00094             delay(JVC_REPEAT_SPACE / MICROS_IN_ONE_MILLI);
00095         }
00096     }
00097 }
