00099 /*
00100  * First check for right data length
00101  * Next check start bit
00102  * Next try the decode
00103  */
00104 bool IRrecv::decodeJVC() {
00105 
00106     // Check we have the right amount of data (36 or 34). The +4 is for initial gap, start bit mark and space + stop bit mark. +2 is for repeats
00107     if (decodedIRData.rawDataPtr->rawlen != ((2 * JVC_BITS) + 4) && decodedIRData.rawDataPtr->rawlen != ((2 * JVC_BITS) + 2)) {
00108         IR_DEBUG_PRINT(F("JVC: "));
00109         IR_DEBUG_PRINT("Data length=");
00110         IR_DEBUG_PRINT(decodedIRData.rawDataPtr->rawlen);
00111         IR_DEBUG_PRINTLN(" is not 34 or 36");
00112         return false;
00113     }
00114 
00115     if (decodedIRData.rawDataPtr->rawlen == ((2 * JVC_BITS) + 2)) {
00116         /*
00117          * Check for repeat
00118          * Check leading space and first and last mark length
00119          */
00120         if (decodedIRData.rawDataPtr->rawbuf[0] < ((JVC_REPEAT_SPACE + (JVC_REPEAT_SPACE / 4) / MICROS_PER_TICK))
00121                 && matchMark(decodedIRData.rawDataPtr->rawbuf[1], JVC_BIT_MARK)
00122                 && matchMark(decodedIRData.rawDataPtr->rawbuf[decodedIRData.rawDataPtr->rawlen - 1], JVC_BIT_MARK)) {
00123             /*
00124              * We have a repeat here, so do not check for start bit
00125              */
00126             decodedIRData.flags = IRDATA_FLAGS_IS_REPEAT | IRDATA_FLAGS_IS_LSB_FIRST;
00127             decodedIRData.address = lastDecodedAddress;
00128             decodedIRData.command = lastDecodedCommand;
00129             decodedIRData.protocol = JVC;
00130         }
00131     } else {
00132 
00133         // Check header "mark" and "space"
00134         if (!matchMark(decodedIRData.rawDataPtr->rawbuf[1], JVC_HEADER_MARK)
00135                 || !matchSpace(decodedIRData.rawDataPtr->rawbuf[2], JVC_HEADER_SPACE)) {
00136             IR_DEBUG_PRINT("JVC: ");
00137             IR_DEBUG_PRINTLN("Header mark or space length is wrong");
00138             return false;
00139         }
00140 
00141         if (!decodePulseDistanceData(JVC_BITS, 3, JVC_BIT_MARK, JVC_ONE_SPACE, JVC_ZERO_SPACE, PROTOCOL_IS_LSB_FIRST)) {
00142             IR_DEBUG_PRINT(F("JVC: "));
00143             IR_DEBUG_PRINTLN(F("Decode failed"));
00144             return false;
00145         }
00146 
00147         // Success
00148 //    decodedIRData.flags = IRDATA_FLAGS_IS_LSB_FIRST; // Not required, since this is the start value
00149         uint8_t tCommand = decodedIRData.decodedRawData >> JVC_ADDRESS_BITS;  // upper 8 bits of LSB first value
00150         uint8_t tAddress = decodedIRData.decodedRawData & 0xFF;    // lowest 8 bit of LSB first value
00151 
00152         decodedIRData.command = tCommand;
00153         decodedIRData.address = tAddress;
00154         decodedIRData.numberOfBits = JVC_BITS;
00155         decodedIRData.protocol = JVC;
00156     }
00157 
00158     return true;
00159 }
