00161 bool IRrecv::decodeJVCMSB(decode_results *aResults) {
00162     unsigned int offset = 1; // Skip first space
00163 
00164     // Check for repeat
00165     if ((aResults->rawlen - 1 == 33) && matchMark(aResults->rawbuf[offset], JVC_BIT_MARK)
00166             && matchMark(aResults->rawbuf[aResults->rawlen - 1], JVC_BIT_MARK)) {
00167         aResults->bits = 0;
00168         aResults->value = 0xFFFFFFFF;
00169         decodedIRData.flags = IRDATA_FLAGS_IS_REPEAT;
00170         decodedIRData.protocol = JVC;
00171         return true;
00172     }
00173 
00174     // Initial mark
00175     if (!matchMark(aResults->rawbuf[offset], JVC_HEADER_MARK)) {
00176         return false;
00177     }
00178     offset++;
00179 
00180     // Check we have enough data - +3 for start bit mark and space + stop bit mark
00181     if (aResults->rawlen <= (2 * JVC_BITS) + 3) {
00182         IR_DEBUG_PRINT("Data length=");
00183         IR_DEBUG_PRINT(aResults->rawlen);
00184         IR_DEBUG_PRINTLN(" is too small. >= 36 is required.");
00185 
00186         return false;
00187     }
00188 
00189     // Initial space
00190     if (!matchSpace(aResults->rawbuf[offset], JVC_HEADER_SPACE)) {
00191         return false;
00192     }
00193     offset++;
00194 
00195     if (!decodePulseDistanceData(JVC_BITS, offset, JVC_BIT_MARK, JVC_ONE_SPACE, JVC_ZERO_SPACE, PROTOCOL_IS_MSB_FIRST)) {
00196         return false;
00197     }
00198 
00199     // Stop bit
00200     if (!matchMark(aResults->rawbuf[offset + (2 * JVC_BITS)], JVC_BIT_MARK)) {
00201         IR_DEBUG_PRINTLN(F("Stop bit mark length is wrong"));
00202         return false;
00203     }
00204 
00205     // Success
00206     aResults->value = decodedIRData.decodedRawData;
00207     aResults->bits = JVC_BITS;
00208     aResults->decode_type = JVC;
00209     decodedIRData.protocol = JVC;
00210 
00211     return true;
00212 }
