00224 void IRsend::sendJVCMSB(unsigned long data, int nbits, bool repeat) {
00225     // Set IR carrier frequency
00226     enableIROut(JVC_KHZ);
00227 
00228     // Only send the Header if this is NOT a repeat command
00229     if (!repeat) {
00230         mark(JVC_HEADER_MARK);
00231         space(JVC_HEADER_SPACE);
00232     }
00233 
00234     // Old version with MSB first Data
00235     sendPulseDistanceWidthData(JVC_BIT_MARK, JVC_ONE_SPACE, JVC_BIT_MARK, JVC_ZERO_SPACE, data, nbits, PROTOCOL_IS_MSB_FIRST,
00236     SEND_STOP_BIT);
00237 }
