00044 //==============================================================================
00045 //       PPPP    AAA   N   N   AAA    SSSS   OOO   N   N  IIIII   CCCC
00046 //       P   P  A   A  NN  N  A   A  S      O   O  NN  N    I    C
00047 //       PPPP   AAAAA  N N N  AAAAA   SSS   O   O  N N N    I    C
00048 //       P      A   A  N  NN  A   A      S  O   O  N  NN    I    C
00049 //       P      A   A  N   N  A   A  SSSS    OOO   N   N  IIIII   CCCC
00050 //==============================================================================
00051 // see: http://www.hifi-remote.com/johnsfine/DecodeIR.html#Panasonic and http://www.hifi-remote.com/johnsfine/DecodeIR.html#Kaseikyo
00052 // IRP notation: {37k,432}<1,-1|1,-3>(8,-4,M:8,N:8,X:4,D:4,S:8,F:8,G:8,1,-173)+ {X=M:4:0^M:4:4^N:4:0^N:4:4}
00053 // see: http://www.remotecentral.com/cgi-bin/mboard/rc-pronto/thread.cgi?26152
00054 // The first two (8-bit) bytes are always 2 and 32 (These identify Panasonic within the Kaseikyo standard)
00055 // The next two bytes are 4 independent 4-bit fields or Device and Subdevice
00056 // The second to last byte is the function and the last byte is xor of the three bytes before it.
00057 // 0_______ 1_______  2______ 3_______ 4_______ 5
00058 // 76543210 76543210 76543210 76543210 76543210 76543210
00059 // 00000010 00100000 Dev____  Sub Dev  Fun____  XOR( B2, B3, B4)
00060 // LSB first, start bit + 16 Vendor + 4 Parity(of vendor) + 4 Genre1 + 4 Genre2 + 10 Command + 2 ID + 8 Parity + stop bit
00061 // We reduce it to: start bit + 16 Vendor + 16 Address + 8 Command + 8 Parity + stop bit
00062 //
00063 #define KASEIKYO_VENDOR_ID_BITS     16
00064 #define KASEIKYO_VENDOR_ID_PARITY_BITS   4
00065 #define KASEIKYO_ADDRESS_BITS       12
00066 #define KASEIKYO_COMMAND_BITS       8
00067 #define KASEIKYO_PARITY_BITS        8
00068 #define KASEIKYO_BITS               (KASEIKYO_VENDOR_ID_BITS + KASEIKYO_VENDOR_ID_PARITY_BITS + KASEIKYO_ADDRESS_BITS + KASEIKYO_COMMAND_BITS + KASEIKYO_PARITY_BITS)
00069 #define KASEIKYO_UNIT               432 // 16 pulses of 37 kHz (432,432432)  - Pronto 0x70 / 0x10
00070 
00071 #define KASEIKYO_HEADER_MARK        (8 * KASEIKYO_UNIT) // 3456
00072 #define KASEIKYO_HEADER_SPACE       (4 * KASEIKYO_UNIT) // 1728
00073 
00074 #define KASEIKYO_BIT_MARK           KASEIKYO_UNIT
00075 #define KASEIKYO_ONE_SPACE          (3 * KASEIKYO_UNIT) // 1296
00076 #define KASEIKYO_ZERO_SPACE         KASEIKYO_UNIT
00077 
00078 #define KASEIKYO_AVERAGE_DURATION   56000
00079 #define KASEIKYO_REPEAT_PERIOD      130000
00080 #define KASEIKYO_REPEAT_SPACE       (KASEIKYO_REPEAT_PERIOD - KASEIKYO_AVERAGE_DURATION) // 74 ms
00081 
00082 // for old decoder
00083 #define KASEIKYO_DATA_BITS          32
00084 
00085 //+=============================================================================
00086 /*
00087  * Send with LSB first
00088  * Address is sub-device << 8 + device
00089  */
00090 void IRsend::sendKaseikyo(uint16_t aAddress, uint8_t aCommand, uint_fast8_t aNumberOfRepeats, uint16_t aVendorCode) {
00091     // Set IR carrier frequency
00092     enableIROut(KASEIKYO_KHZ); // 37 kHz
00093 
00094     uint_fast8_t tNumberOfCommands = aNumberOfRepeats + 1;
00095     while (tNumberOfCommands > 0) {
00096 
00097         // Header
00098         mark(KASEIKYO_HEADER_MARK);
00099         space(KASEIKYO_HEADER_SPACE);
00100 
00101         // Vendor ID
00102         sendPulseDistanceWidthData(KASEIKYO_BIT_MARK, KASEIKYO_ONE_SPACE, KASEIKYO_BIT_MARK, KASEIKYO_ZERO_SPACE, aVendorCode,
00103         KASEIKYO_VENDOR_ID_BITS, PROTOCOL_IS_LSB_FIRST);
00104 
00105         // Vendor Parity
00106         uint8_t tVendorParity = aVendorCode ^ (aVendorCode >> 8);
00107         tVendorParity = (tVendorParity ^ (tVendorParity >> 4)) & 0xF;
00108 
00109         LongUnion tSendValue;
00110         tSendValue.UWord.LowWord = aAddress << KASEIKYO_VENDOR_ID_PARITY_BITS;
00111         tSendValue.UByte.LowByte |= tVendorParity; // set low nibble to parity
00112         tSendValue.UByte.MidHighByte = aCommand;
00113         tSendValue.UByte.HighByte = aCommand ^ tSendValue.UByte.LowByte ^ tSendValue.UByte.MidLowByte; // Parity
00114 
00115         // Send address (device and subdevice) + command + parity + Stop bit
00116         sendPulseDistanceWidthData(KASEIKYO_BIT_MARK, KASEIKYO_ONE_SPACE, KASEIKYO_BIT_MARK, KASEIKYO_ZERO_SPACE, tSendValue.ULong,
00117         KASEIKYO_ADDRESS_BITS + KASEIKYO_VENDOR_ID_PARITY_BITS + KASEIKYO_COMMAND_BITS + KASEIKYO_PARITY_BITS,
00118         PROTOCOL_IS_LSB_FIRST, SEND_STOP_BIT);
00119 
00120         tNumberOfCommands--;
00121         // skip last delay!
00122         if (tNumberOfCommands > 0) {
00123             // send repeated command in a fixed raster
00124             delay(KASEIKYO_REPEAT_SPACE / MICROS_IN_ONE_MILLI);
00125         }
00126     }
00127 }
