00164 /*
00165  * Tested with my Panasonic DVD/TV remote
00166  */
00167 bool IRrecv::decodeKaseikyo() {
00168 
00169     decode_type_t tProtocol;
00170     // Check we have enough data (100)- +4 for initial gap, start bit mark and space + stop bit mark
00171     if (decodedIRData.rawDataPtr->rawlen != ((2 * KASEIKYO_BITS) + 4)) {
00172         IR_DEBUG_PRINT(F("Kaseikyo: "));
00173         IR_DEBUG_PRINT("Data length=");
00174         IR_DEBUG_PRINT(decodedIRData.rawDataPtr->rawlen);
00175         IR_DEBUG_PRINTLN(" is not 100");
00176         return false;
00177     }
00178 
00179     if (!matchMark(decodedIRData.rawDataPtr->rawbuf[1], KASEIKYO_HEADER_MARK)) {
00180         IR_DEBUG_PRINT("Kaseikyo: ");
00181         IR_DEBUG_PRINTLN("Header mark length is wrong");
00182         return false;
00183     }
00184 
00185     if (!matchMark(decodedIRData.rawDataPtr->rawbuf[2], KASEIKYO_HEADER_SPACE)) {
00186         IR_DEBUG_PRINT("Kaseikyo: ");
00187         IR_DEBUG_PRINTLN("Header space length is wrong");
00188         return false;
00189     }
00190 
00191     // decode Vendor ID
00192     if (!decodePulseDistanceData(KASEIKYO_VENDOR_ID_BITS, 3, KASEIKYO_BIT_MARK, KASEIKYO_ONE_SPACE, KASEIKYO_ZERO_SPACE,
00193     PROTOCOL_IS_LSB_FIRST)) {
00194         IR_DEBUG_PRINT("Kaseikyo: ");
00195         IR_DEBUG_PRINTLN("Vendor ID decode failed");
00196         return false;
00197     }
00198 
00199     uint16_t tVendorId = decodedIRData.decodedRawData;
00200     if (tVendorId == PANASONIC_VENDOR_ID_CODE) {
00201         tProtocol = PANASONIC;
00202     } else if (tVendorId == SHARP_VENDOR_ID_CODE) {
00203         tProtocol = KASEIKYO_SHARP;
00204     } else if (tVendorId == DENON_VENDOR_ID_CODE) {
00205         tProtocol = KASEIKYO_DENON;
00206     } else if (tVendorId == JVC_VENDOR_ID_CODE) {
00207         tProtocol = KASEIKYO_JVC;
00208     } else if (tVendorId == MITSUBISHI_VENDOR_ID_CODE) {
00209         tProtocol = KASEIKYO_MITSUBISHI;
00210     } else {
00211         tProtocol = KASEIKYO;
00212     }
00213 
00214     // Vendor Parity
00215     uint8_t tVendorParity = tVendorId ^ (tVendorId >> 8);
00216     tVendorParity = (tVendorParity ^ (tVendorParity >> 4)) & 0xF;
00217 
00218     // decode address (device and subdevice) + command + parity
00219     if (!decodePulseDistanceData(
00220     KASEIKYO_VENDOR_ID_PARITY_BITS + KASEIKYO_ADDRESS_BITS + KASEIKYO_COMMAND_BITS + KASEIKYO_PARITY_BITS,
00221             3 + (2 * KASEIKYO_VENDOR_ID_BITS), KASEIKYO_BIT_MARK, KASEIKYO_ONE_SPACE,
00222             KASEIKYO_ZERO_SPACE, PROTOCOL_IS_LSB_FIRST)) {
00223         IR_DEBUG_PRINT("Kaseikyo: ");
00224         IR_DEBUG_PRINTLN("Address, command + parity decode failed");
00225         return false;
00226     }
00227 
00228     // Success
00229 //    decodedIRData.flags = IRDATA_FLAGS_IS_LSB_FIRST; // Not required, since this is the start value
00230     LongUnion tValue;
00231     tValue.ULong = decodedIRData.decodedRawData;
00232     decodedIRData.address = (tValue.UWord.LowWord >> KASEIKYO_VENDOR_ID_PARITY_BITS); // remove vendor parity
00233     decodedIRData.command = tValue.UByte.MidHighByte;
00234     uint8_t tParity = tValue.UByte.LowByte ^ tValue.UByte.MidLowByte ^ tValue.UByte.MidHighByte;
00235 
00236     if (tVendorParity != (tValue.UByte.LowByte & 0xF)) {
00237         IR_DEBUG_PRINT("Kaseikyo: ");
00238         IR_DEBUG_PRINT("4 bit VendorID Parity is not correct. expected=0x");
00239         IR_DEBUG_PRINT(tVendorParity, HEX);
00240         IR_DEBUG_PRINT(" received=0x");
00241         IR_DEBUG_PRINT(decodedIRData.decodedRawData, HEX);
00242         IR_DEBUG_PRINT(" VendorID=0x");
00243         IR_DEBUG_PRINTLN(tVendorId, HEX);
00244         decodedIRData.flags = IRDATA_FLAGS_PARITY_FAILED | IRDATA_FLAGS_IS_LSB_FIRST;
00245     }
00246 
00247     if (tProtocol != KASEIKYO) {
00248         decodedIRData.flags |= IRDATA_FLAGS_EXTRA_INFO;
00249         // Include vendor ID in address
00250         decodedIRData.extra |= tVendorId;
00251     }
00252 
00253     if (tValue.UByte.HighByte != tParity) {
00254         IR_DEBUG_PRINT("Kaseikyo: ");
00255         IR_DEBUG_PRINT("8 bit Parity is not correct. expected=0x");
00256         IR_DEBUG_PRINT(tParity, HEX);
00257         IR_DEBUG_PRINT(" received=0x");
00258         IR_DEBUG_PRINT(decodedIRData.decodedRawData >> KASEIKYO_COMMAND_BITS, HEX);
00259         IR_DEBUG_PRINT(" address=0x");
00260         IR_DEBUG_PRINT(decodedIRData.address, HEX);
00261         IR_DEBUG_PRINT(" command=0x");
00262         IR_DEBUG_PRINTLN(decodedIRData.command, HEX);
00263         decodedIRData.flags |= IRDATA_FLAGS_PARITY_FAILED;
00264     }
00265 
00266     // check for repeat
00267     if (decodedIRData.rawDataPtr->rawbuf[0] < (KASEIKYO_REPEAT_PERIOD / MICROS_PER_TICK)) {
00268         decodedIRData.flags |= IRDATA_FLAGS_IS_REPEAT;
00269     }
00270 
00271     decodedIRData.protocol = tProtocol;
00272 
00273     decodedIRData.numberOfBits = KASEIKYO_BITS;
00274 
00275     return true;
00276 }
