00281 bool IRrecv::decodePanasonicMSB(decode_results *aResults) {
00282     unsigned int offset = 1;
00283 
00284     if (aResults->rawlen < (2 * KASEIKYO_BITS) + 2) {
00285         return false;
00286     }
00287 
00288     if (!matchMark(aResults->rawbuf[offset], KASEIKYO_HEADER_MARK)) {
00289         return false;
00290     }
00291     offset++;
00292     if (!matchMark(aResults->rawbuf[offset], KASEIKYO_HEADER_SPACE)) {
00293         return false;
00294     }
00295     offset++;
00296 
00297     // decode address
00298     if (!decodePulseDistanceData(KASEIKYO_ADDRESS_BITS + KASEIKYO_DATA_BITS, offset, KASEIKYO_BIT_MARK, KASEIKYO_ONE_SPACE,
00299     KASEIKYO_ZERO_SPACE, PROTOCOL_IS_MSB_FIRST)) {
00300         return false;
00301     }
00302 
00303     aResults->bits = KASEIKYO_BITS;
00304     aResults->value = decodedIRData.decodedRawData;
00305     aResults->address = PANASONIC_VENDOR_ID_CODE;
00306     aResults->decode_type = PANASONIC;
00307     decodedIRData.protocol = PANASONIC;
00308 
00309     return true;
00310 }
