00315 void IRsend::sendPanasonic(uint16_t aAddress, uint32_t aData) {
00316     // Set IR carrier frequency
00317     enableIROut(KASEIKYO_KHZ); // 36.7kHz is the correct frequency
00318 
00319     // Header
00320     mark(KASEIKYO_HEADER_MARK);
00321     space(KASEIKYO_HEADER_SPACE);
00322 
00323     // Old version with MSB first Data Address
00324     sendPulseDistanceWidthData(KASEIKYO_BIT_MARK, KASEIKYO_ONE_SPACE, KASEIKYO_BIT_MARK, KASEIKYO_ZERO_SPACE, aAddress,
00325     KASEIKYO_ADDRESS_BITS, PROTOCOL_IS_MSB_FIRST);
00326 
00327     // Old version with MSB first Data Data + stop bit
00328     sendPulseDistanceWidthData(KASEIKYO_BIT_MARK, KASEIKYO_ONE_SPACE, KASEIKYO_BIT_MARK, KASEIKYO_ZERO_SPACE, aData,
00329     KASEIKYO_DATA_BITS, PROTOCOL_IS_MSB_FIRST);
00330 }
