00044 //==============================================================================
00045 //                           N   N  EEEEE   CCCC
00046 //                           NN  N  E      C
00047 //                           N N N  EEE    C
00048 //                           N  NN  E      C
00049 //                           N   N  EEEEE   CCCC
00050 //==============================================================================
00051 // see: https://www.sbprojects.net/knowledge/ir/nec.php
00052 // for Apple see https://en.wikipedia.org/wiki/Apple_Remote
00053 // ONKYO like NEC but 16 independent command bits
00054 // LSB first, 1 start bit + 16 bit address + 8 bit command + 8 bit inverted command + 1 stop bit.
00055 //
00056 #define NEC_ADDRESS_BITS        16 // 16 bit address or 8 bit address and 8 bit inverted address
00057 #define NEC_COMMAND_BITS        16 // Command and inverted command
00058 
00059 #define NEC_BITS                (NEC_ADDRESS_BITS + NEC_COMMAND_BITS)
00060 #define NEC_UNIT                560 // 21.28 periods of 38 kHz
00061 
00062 #define NEC_HEADER_MARK         (16 * NEC_UNIT) // 9000
00063 #define NEC_HEADER_SPACE        (8 * NEC_UNIT)  // 4500
00064 
00065 #define NEC_BIT_MARK            NEC_UNIT
00066 #define NEC_ONE_SPACE           (3 * NEC_UNIT)  // 1690
00067 #define NEC_ZERO_SPACE          NEC_UNIT
00068 
00069 #define NEC_REPEAT_HEADER_SPACE (4 * NEC_UNIT)  // 2250
00070 
00071 #define NEC_AVERAGE_DURATION    62000 // NEC_HEADER_MARK + NEC_HEADER_SPACE + 32 * 2,5 * NEC_UNIT + NEC_UNIT // 2.5 because we assume more zeros than ones
00072 #define NEC_REPEAT_DURATION     (NEC_HEADER_MARK  + NEC_REPEAT_HEADER_SPACE + NEC_BIT_MARK) // 12 ms
00073 #define NEC_REPEAT_PERIOD       110000 // Commands are repeated every 110 ms (measured from start to start) for as long as the key on the remote control is held down.
00074 #define NEC_REPEAT_SPACE        (NEC_REPEAT_PERIOD - NEC_AVERAGE_DURATION) // 48 ms
00075 
00076 #define APPLE_ADDRESS           0x87EE
00077 //+=============================================================================
00078 /*
00079  * Send repeat
00080  * Repeat commands should be sent in a 110 ms raster.
00081  */
00082 void IRsend::sendNECRepeat() {
00083     enableIROut(NEC_KHZ); // 38 kHz
00084     mark(NEC_HEADER_MARK);
00085     space(NEC_REPEAT_HEADER_SPACE);
00086     mark(NEC_BIT_MARK);
00087 //    ledOff(); // Always end with the LED off
00088 }
00089 
00090 /*
00091  * Repeat commands should be sent in a 110 ms raster.
00092  * There is NO delay after the last sent repeat!
00093  * https://www.sbprojects.net/knowledge/ir/nec.php
00094  * @param aIsRepeat if true, send only one repeat frame without leading and trailing space
00095  */
00096 void IRsend::sendNEC(uint16_t aAddress, uint8_t aCommand, uint_fast8_t aNumberOfRepeats, bool aIsRepeat) {
00097 
00098     LongUnion tRawData;
00099 
00100     // Address 16 bit LSB first
00101     if ((aAddress & 0xFF00) == 0) {
00102         // assume 8 bit address -> send 8 address bits and then 8 inverted address bits LSB first
00103         tRawData.UByte.LowByte = aAddress;
00104         tRawData.UByte.MidLowByte = ~tRawData.UByte.LowByte;
00105     } else {
00106         tRawData.UWord.LowWord = aAddress;
00107     }
00108 
00109     // send 8 command bits and then 8 inverted command bits LSB first
00110     tRawData.UByte.MidHighByte = aCommand;
00111     tRawData.UByte.HighByte = ~aCommand;
00112 
00113     sendNECRaw(tRawData.ULong, aNumberOfRepeats, aIsRepeat);
00114 }
00115 
00116 /*
00117  * Repeat commands should be sent in a 110 ms raster.
00118  * There is NO delay after the last sent repeat!
00119  * @param aIsRepeat if true, send only one repeat frame without leading and trailing space
00120  */
00121 void IRsend::sendOnkyo(uint16_t aAddress, uint16_t aCommand, uint_fast8_t aNumberOfRepeats, bool aIsRepeat) {
00122 
00123     LongUnion tRawData;
00124 
00125     // Address 16 bit LSB first
00126     tRawData.UWord.LowWord = aAddress;
00127     // Command 16 bit LSB first
00128     tRawData.UWord.HighWord = aCommand;
00129 
00130     sendNECRaw(tRawData.ULong, aNumberOfRepeats, aIsRepeat);
00131 }
