00155 void IRsend::sendNECRaw(uint32_t aRawData, uint_fast8_t aNumberOfRepeats, bool aIsRepeat) {
00156     if (aIsRepeat) {
00157         sendNECRepeat();
00158         return;
00159     }
00160     // Set IR carrier frequency
00161     enableIROut(NEC_KHZ);
00162 
00163     // Header
00164     mark(NEC_HEADER_MARK);
00165     space(NEC_HEADER_SPACE);
00166 
00167     // LSB first + stop bit
00168     sendPulseDistanceWidthData(NEC_BIT_MARK, NEC_ONE_SPACE, NEC_BIT_MARK, NEC_ZERO_SPACE, aRawData, NEC_BITS, PROTOCOL_IS_LSB_FIRST,
00169     SEND_STOP_BIT);
00170 
00171     for (uint_fast8_t i = 0; i < aNumberOfRepeats; ++i) {
00172         // send repeat in a 110 ms raster
00173         if (i == 0) {
00174             delay(NEC_REPEAT_SPACE / MICROS_IN_ONE_MILLI);
00175         } else {
00176             delay((NEC_REPEAT_PERIOD - NEC_REPEAT_DURATION) / MICROS_IN_ONE_MILLI);
00177         }
00178         // send repeat
00179         sendNECRepeat();
00180     }
00181 }
