00183 //+=============================================================================
00184 // NECs have a repeat only 4 items long
00185 //
00186 /*
00187  * First check for right data length
00188  * Next check start bit
00189  * Next try the decode
00190  * Last check stop bit
00191  *
00192  * Decodes also Apple
00193  */
00194 bool IRrecv::decodeNEC() {
00195 
00196     // Check we have the right amount of data (68). The +4 is for initial gap, start bit mark and space + stop bit mark.
00197     if (decodedIRData.rawDataPtr->rawlen != ((2 * NEC_BITS) + 4) && (decodedIRData.rawDataPtr->rawlen != 4)) {
00198         IR_DEBUG_PRINT(F("NEC: "));
00199         IR_DEBUG_PRINT("Data length=");
00200         IR_DEBUG_PRINT(decodedIRData.rawDataPtr->rawlen);
00201         IR_DEBUG_PRINTLN(" is not 68 or 4");
00202         return false;
00203     }
00204 
00205     // Check header "mark" this must be done for repeat and data
00206     if (!matchMark(decodedIRData.rawDataPtr->rawbuf[1], NEC_HEADER_MARK)) {
00207         return false;
00208     }
00209 
00210     // Check for repeat - here we have another header space length
00211     if (decodedIRData.rawDataPtr->rawlen == 4) {
00212         if (matchSpace(decodedIRData.rawDataPtr->rawbuf[2], NEC_REPEAT_HEADER_SPACE)
00213                 && matchMark(decodedIRData.rawDataPtr->rawbuf[3], NEC_BIT_MARK)) {
00214             decodedIRData.flags = IRDATA_FLAGS_IS_REPEAT | IRDATA_FLAGS_IS_LSB_FIRST;
00215             decodedIRData.address = lastDecodedAddress;
00216             decodedIRData.command = lastDecodedCommand;
00217             decodedIRData.protocol = lastDecodedProtocol;
00218             return true;
00219         }
00220         return false;
00221     }
00222 
00223     // Check command header space
00224     if (!matchSpace(decodedIRData.rawDataPtr->rawbuf[2], NEC_HEADER_SPACE)) {
00225         IR_DEBUG_PRINT(F("NEC: "));
00226         IR_DEBUG_PRINTLN(F("Header space length is wrong"));
00227         return false;
00228     }
00229 
00230     if (!decodePulseDistanceData(NEC_BITS, 3, NEC_BIT_MARK, NEC_ONE_SPACE, NEC_ZERO_SPACE, PROTOCOL_IS_LSB_FIRST)) {
00231         IR_DEBUG_PRINT(F("NEC: "));
00232         IR_DEBUG_PRINTLN(F("Decode failed"));
00233         return false;
00234     }
00235 
00236     // Stop bit
00237     if (!matchMark(decodedIRData.rawDataPtr->rawbuf[3 + (2 * NEC_BITS)], NEC_BIT_MARK)) {
00238         IR_DEBUG_PRINT(F("NEC: "));
00239         IR_DEBUG_PRINTLN(F("Stop bit mark length is wrong"));
00240         return false;
00241     }
00242 
00243     // Success
00244 //    decodedIRData.flags = IRDATA_FLAGS_IS_LSB_FIRST; // Not required, since this is the start value
00245     LongUnion tValue;
00246     tValue.ULong = decodedIRData.decodedRawData;
00247     decodedIRData.command = tValue.UByte.MidHighByte; // 8 bit
00248     // Address
00249     if (tValue.UWord.LowWord == APPLE_ADDRESS) {
00250         /*
00251          * Apple
00252          */
00253         decodedIRData.protocol = APPLE;
00254         decodedIRData.address = tValue.UByte.HighByte;
00255 
00256     } else {
00257         /*
00258          * NEC
00259          */
00260         if (tValue.UByte.LowByte == (uint8_t) (~tValue.UByte.MidLowByte)) {
00261             // standard 8 bit address NEC protocol
00262             decodedIRData.address = tValue.UByte.LowByte; // first 8 bit
00263         } else {
00264             // extended NEC protocol
00265             decodedIRData.address = tValue.UWord.LowWord; // first 16 bit
00266         }
00267         // Check for command if it is 8 bit NEC or 16 bit ONKYO
00268         if (tValue.UByte.MidHighByte == (uint8_t) (~tValue.UByte.HighByte)) {
00269             decodedIRData.protocol = NEC;
00270         } else {
00271             decodedIRData.protocol = ONKYO;
00272             decodedIRData.command = tValue.UWord.HighWord; // 16 bit command
00273 
00274             /*
00275              * Old NEC plausibility check below, now it is just ONKYO :-)
00276              */
00277 //            IR_DEBUG_PRINT(F("NEC: "));
00278 //            IR_DEBUG_PRINT(F("Command=0x"));
00279 //            IR_DEBUG_PRINT(tValue.UByte.MidHighByte, HEX);
00280 //            IR_DEBUG_PRINT(F(" is not inverted value of 0x"));
00281 //            IR_DEBUG_PRINTLN(tValue.UByte.HighByte, HEX);
00282 //            decodedIRData.flags = IRDATA_FLAGS_PARITY_FAILED | IRDATA_FLAGS_IS_LSB_FIRST;
00283         }
00284     }
00285     decodedIRData.numberOfBits = NEC_BITS;
00286 
00287     return true;
00288 }
