00290 bool IRrecv::decodeNECMSB(decode_results *aResults) {
00291     unsigned int offset = 1;  // Index in to results; Skip first space.
00292 
00293 // Check header "mark"
00294     if (!matchMark(aResults->rawbuf[offset], NEC_HEADER_MARK)) {
00295         return false;
00296     }
00297     offset++;
00298 
00299 // Check for repeat
00300     if ((aResults->rawlen == 4) && matchSpace(aResults->rawbuf[offset], NEC_REPEAT_HEADER_SPACE)
00301             && matchMark(aResults->rawbuf[offset + 1], NEC_BIT_MARK)) {
00302         aResults->bits = 0;
00303         aResults->value = 0xFFFFFFFF;
00304         decodedIRData.flags |= IRDATA_FLAGS_IS_REPEAT;
00305         decodedIRData.protocol = NEC;
00306         return true;
00307     }
00308 
00309     // Check we have the right amount of data (32). +4 for initial gap, start bit mark and space + stop bit mark
00310     if (aResults->rawlen != (2 * NEC_BITS) + 4) {
00311         IR_DEBUG_PRINT("NEC MSB: ");
00312         IR_DEBUG_PRINT("Data length=");
00313         IR_DEBUG_PRINT(aResults->rawlen);
00314         IR_DEBUG_PRINTLN(" is not 68");
00315         return false;
00316     }
00317 
00318 // Check header "space"
00319     if (!matchSpace(aResults->rawbuf[offset], NEC_HEADER_SPACE)) {
00320         IR_DEBUG_PRINT("NEC MSB: ");
00321         IR_DEBUG_PRINTLN("Header space length is wrong");
00322         return false;
00323     }
00324     offset++;
00325 
00326     if (!decodePulseDistanceData(NEC_BITS, offset, NEC_BIT_MARK, NEC_ONE_SPACE, NEC_ZERO_SPACE, PROTOCOL_IS_MSB_FIRST)) {
00327         IR_DEBUG_PRINT(F("NEC MSB: "));
00328         IR_DEBUG_PRINTLN(F("Decode failed"));
00329         return false;
00330     }
00331 
00332     // Stop bit
00333     if (!matchMark(aResults->rawbuf[offset + (2 * NEC_BITS)], NEC_BIT_MARK)) {
00334         IR_DEBUG_PRINT("NEC MSB: ");
00335         IR_DEBUG_PRINTLN(F("Stop bit mark length is wrong"));
00336         return false;
00337     }
00338 
00339 // Success
00340     aResults->value = decodedIRData.decodedRawData;
00341     aResults->bits = NEC_BITS;
00342     aResults->decode_type = NEC;
00343     decodedIRData.protocol = NEC;
00344 
00345     return true;
00346 }
