00358 void IRsend::sendNECMSB(uint32_t data, uint8_t nbits, bool repeat) {
00359     // Set IR carrier frequency
00360     enableIROut(NEC_KHZ);
00361 
00362     if (data == 0xFFFFFFFF || repeat) {
00363         sendNECRepeat();
00364         return;
00365     }
00366 
00367     // Header
00368     mark(NEC_HEADER_MARK);
00369     space(NEC_HEADER_SPACE);
00370 
00371     // Old version with MSB first Data + stop bit
00372     sendPulseDistanceWidthData(NEC_BIT_MARK, NEC_ONE_SPACE, NEC_BIT_MARK, NEC_ZERO_SPACE, data, nbits, PROTOCOL_IS_MSB_FIRST,
00373     SEND_STOP_BIT);
00374 }
