00046 //==============================================================================
00047 //     RRRR    CCCC  55555
00048 //     R   R  C      5
00049 //     RRRR   C      5555
00050 //     R  R   C          5
00051 //     R   R   CCCC  5555
00052 //==============================================================================
00053 //
00054 // see: https://www.sbprojects.net/knowledge/ir/rc5.php
00055 // https://en.wikipedia.org/wiki/Manchester_code
00056 // mark->space => 0
00057 // space->mark => 1
00058 // MSB first 1 start bit, 1 field bit, 1 toggle bit + 5 bit address + 6 bit command (6 bit command plus one field bit for RC5X), no stop bit
00059 // duty factor is 25%,
00060 //
00061 #define RC5_ADDRESS_BITS        5
00062 #define RC5_COMMAND_BITS        6
00063 #define RC5_COMMAND_FIELD_BIT   1
00064 #define RC5_TOGGLE_BIT          1
00065 
00066 #define RC5_BITS            (RC5_COMMAND_FIELD_BIT + RC5_TOGGLE_BIT + RC5_ADDRESS_BITS + RC5_COMMAND_BITS) // 13
00067 
00068 #define RC5_UNIT            889 // 32 periods of 36 kHz (888.8888)
00069 
00070 #define MIN_RC5_MARKS       ((RC5_BITS + 1) / 2) // 7
00071 
00072 #define RC5_DURATION        (15L * RC5_UNIT) // 13335
00073 #define RC5_REPEAT_PERIOD   (128L * RC5_UNIT) // 113792
00074 #define RC5_REPEAT_SPACE    (RC5_REPEAT_PERIOD - RC5_DURATION) // 100 ms
00075 
00079 void IRsend::sendRC5(uint8_t aAddress, uint8_t aCommand, uint_fast8_t aNumberOfRepeats, bool aEnableAutomaticToggle) {
00080     // Set IR carrier frequency
00081     enableIROut(RC5_RC6_KHZ);
00082 
00083     uint16_t tIRData = ((aAddress & 0x1F) << RC5_COMMAND_BITS);
00084 
00085     if (aCommand < 0x40) {
00086         // set field bit to lower field / set inverted upper command bit
00087         tIRData |= 1 << (RC5_TOGGLE_BIT + RC5_ADDRESS_BITS + RC5_COMMAND_BITS);
00088     } else {
00089         // let field bit zero
00090         aCommand &= 0x3F;
00091     }
00092 
00093     tIRData |= aCommand;
00094 
00095     if (aEnableAutomaticToggle) {
00096         if (sLastSendToggleValue == 0) {
00097             sLastSendToggleValue = 1;
00098             // set toggled bit
00099             tIRData |= 1 << (RC5_ADDRESS_BITS + RC5_COMMAND_BITS);
00100         } else {
00101             sLastSendToggleValue = 0;
00102         }
00103     }
00104 
00105     uint_fast8_t tNumberOfCommands = aNumberOfRepeats + 1;
00106     while (tNumberOfCommands > 0) {
00107 
00108         // start bit is sent by sendBiphaseData
00109         sendBiphaseData(RC5_UNIT, tIRData, RC5_BITS);
00110 
00111         tNumberOfCommands--;
00112         // skip last delay!
00113         if (tNumberOfCommands > 0) {
00114             // send repeated command in a fixed raster
00115             delay(RC5_REPEAT_SPACE / MICROS_IN_ONE_MILLI);
00116         }
00117     }
00118 }
