00132 bool IRrecv::decodeRC5() {
00133     uint8_t tBitIndex;
00134     uint32_t tDecodedRawData = 0;
00135 
00136     // Set Biphase decoding start values
00137     initBiphaselevel(1, RC5_UNIT); // Skip gap space
00138 
00139     // Check we have the right amount of data (11 to 26). The +2 is for initial gap and start bit mark.
00140     if (decodedIRData.rawDataPtr->rawlen < MIN_RC5_MARKS + 2 && decodedIRData.rawDataPtr->rawlen > ((2 * RC5_BITS) + 2)) {
00141         // no debug output, since this check is mainly to determine the received protocol
00142         IR_DEBUG_PRINT(F("RC5: "));
00143         IR_DEBUG_PRINT("Data length=");
00144         IR_DEBUG_PRINT(decodedIRData.rawDataPtr->rawlen);
00145         IR_DEBUG_PRINTLN(" is not between 11 and 26");
00146         return false;
00147     }
00148 
00149 // Check start bit, the first space is included in the gap
00150     if (getBiphaselevel() != MARK) {
00151         IR_DEBUG_PRINT(F("RC5: "));
00152         IR_DEBUG_PRINTLN("first getBiphaselevel() is not MARK");
00153         return false;
00154     }
00155 
00156     /*
00157      * Get data bits - MSB first
00158      */
00159     for (tBitIndex = 0; sBiphaseDecodeRawbuffOffset < decodedIRData.rawDataPtr->rawlen; tBitIndex++) {
00160         uint8_t tStartLevel = getBiphaselevel();
00161         uint8_t tEndLevel = getBiphaselevel();
00162 
00163         if ((tStartLevel == SPACE) && (tEndLevel == MARK)) {
00164             // we have a space to mark transition here
00165             tDecodedRawData = (tDecodedRawData << 1) | 1;
00166         } else if ((tStartLevel == MARK) && (tEndLevel == SPACE)) {
00167             // we have a mark to space transition here
00168             tDecodedRawData = (tDecodedRawData << 1) | 0;
00169         } else {
00170             // IR_TRACE_PRINT since I saw this too often
00171             IR_DEBUG_PRINT(F("RC5: "));
00172             IR_DEBUG_PRINTLN(F("Decode failed"));
00173             return false;
00174         }
00175     }
00176 
00177     // Success
00178     decodedIRData.numberOfBits = tBitIndex; // must be RC5_BITS
00179 
00180     LongUnion tValue;
00181     tValue.ULong = tDecodedRawData;
00182     decodedIRData.decodedRawData = tDecodedRawData;
00183     decodedIRData.command = tValue.UByte.LowByte & 0x3F;
00184     decodedIRData.address = (tValue.UWord.LowWord >> RC5_COMMAND_BITS) & 0x1F;
00185 
00186     // Get the inverted 7. command bit for RC5X, the inverted value is always 1 for RC5 and serves as a second start bit.
00187     if ((tValue.UWord.LowWord & (1 << (RC5_TOGGLE_BIT + RC5_ADDRESS_BITS + RC5_COMMAND_BITS))) == 0) {
00188         decodedIRData.command += 0x40;
00189     }
00190 
00191     decodedIRData.flags = IRDATA_FLAGS_IS_MSB_FIRST;
00192     if (tValue.UByte.MidLowByte & 0x8) {
00193         decodedIRData.flags = IRDATA_TOGGLE_BIT_MASK | IRDATA_FLAGS_IS_MSB_FIRST;
00194     }
00195 
00196     // check for repeat
00197     if (decodedIRData.rawDataPtr->rawbuf[0] < (RC5_REPEAT_PERIOD / MICROS_PER_TICK)) {
00198         decodedIRData.flags |= IRDATA_FLAGS_IS_REPEAT;
00199     }
00200 
00201     decodedIRData.protocol = RC5;
00202     return true;
00203 }
