00205 //+=============================================================================
00206 // RRRR    CCCC   6666
00207 // R   R  C      6
00208 // RRRR   C      6666
00209 // R  R   C      6   6
00210 // R   R   CCCC   666
00211 //
00212 //
00213 // mark->space => 1
00214 // space->mark => 0
00215 // https://www.sbprojects.net/knowledge/ir/rc6.php
00216 // https://www.mikrocontroller.net/articles/IRMP_-_english#RC6_.2B_RC6A
00217 // https://en.wikipedia.org/wiki/Manchester_code
00218 
00219 #define MIN_RC6_SAMPLES         1
00220 
00221 #define RC6_RPT_LENGTH      46000
00222 
00223 #define RC6_LEADING_BIT         1
00224 #define RC6_MODE_BITS           3 // never seen others than all 0 for Philips TV
00225 #define RC6_TOGGLE_BIT          1 // toggles at every key press. Can be used to distinguish repeats from 2 key presses.
00226 #define RC6_ADDRESS_BITS        8
00227 #define RC6_COMMAND_BITS        8
00228 
00229 #define RC6_BITS            (RC6_LEADING_BIT + RC6_MODE_BITS + RC6_TOGGLE_BIT + RC6_ADDRESS_BITS + RC6_COMMAND_BITS) // 13
00230 
00231 #define RC6_UNIT            444 // 16 periods of 36 kHz (444.4444)
00232 
00233 #define RC6_HEADER_MARK     (6 * RC6_UNIT) // 2666
00234 #define RC6_HEADER_SPACE    (2 * RC6_UNIT) // 889
00235 
00236 #define RC6_TRAILING_SPACE  (6 * RC6_UNIT) // 2666
00237 #define MIN_RC6_MARKS       4 + ((RC6_ADDRESS_BITS + RC6_COMMAND_BITS) / 2) // 12, 4 are for preamble
00238 
00239 #define RC6_REPEAT_SPACE    107000 // just a guess but > 2.666ms
00240 
00244 void IRsend::sendRC6(uint32_t aRawData, uint8_t aNumberOfBitsToSend) {
00245 // Set IR carrier frequency
00246     enableIROut(RC5_RC6_KHZ);
00247 
00248 // Header
00249     mark(RC6_HEADER_MARK);
00250     space(RC6_HEADER_SPACE);
00251 
00252 // Start bit
00253     mark(RC6_UNIT);
00254     space(RC6_UNIT);
00255 
00256 // Data MSB first
00257     uint32_t mask = 1UL << (aNumberOfBitsToSend - 1);
00258     for (uint_fast8_t i = 1; mask; i++, mask >>= 1) {
00259         // The fourth bit we send is the "double width toggle bit"
00260         unsigned int t = (i == 4) ? (RC6_UNIT * 2) : (RC6_UNIT);
00261         if (aRawData & mask) {
00262             mark(t);
00263             space(t);
00264         } else {
00265             space(t);
00266             mark(t);
00267         }
00268     }
00269 }
