00307 void IRsend::sendRC6(uint8_t aAddress, uint8_t aCommand, uint_fast8_t aNumberOfRepeats, bool aEnableAutomaticToggle) {
00308 
00309     LongUnion tIRRawData;
00310     tIRRawData.UByte.LowByte = aCommand;
00311     tIRRawData.UByte.MidLowByte = aAddress;
00312 
00313     tIRRawData.UWord.HighWord = 0; // must clear high word
00314     if (aEnableAutomaticToggle) {
00315         if (sLastSendToggleValue == 0) {
00316             sLastSendToggleValue = 1;
00317             // set toggled bit
00318             IR_DEBUG_PRINT(F("Set Toggle "));
00319             tIRRawData.UByte.MidHighByte = 1; // 3 Mode bits are 0
00320         } else {
00321             sLastSendToggleValue = 0;
00322         }
00323     }
00324 
00325     IR_DEBUG_PRINT(F("RC6: "));
00326     IR_DEBUG_PRINT(F("sLastSendToggleValue="));
00327     IR_DEBUG_PRINT(sLastSendToggleValue);
00328     IR_DEBUG_PRINT(F(" RawData="));
00329     IR_DEBUG_PRINTLN(tIRRawData.ULong, HEX);
00330 
00331     uint_fast8_t tNumberOfCommands = aNumberOfRepeats + 1;
00332     while (tNumberOfCommands > 0) {
00333 
00334         // start and leading bits are sent by sendRC6
00335         sendRC6(tIRRawData.ULong, RC6_BITS - 1); // -1 since the leading bit is additionally sent by sendRC6
00336 
00337         tNumberOfCommands--;
00338         // skip last delay!
00339         if (tNumberOfCommands > 0) {
00340             // send repeated command in a fixed raster
00341             delay(RC6_REPEAT_SPACE / MICROS_IN_ONE_MILLI);
00342         }
00343     }
00344 }
