00349 bool IRrecv::decodeRC6() {
00350     uint8_t tBitIndex;
00351     uint32_t tDecodedRawData = 0;
00352 
00353     // Check we have the right amount of data (). The +3 for initial gap, start bit mark and space
00354     if (decodedIRData.rawDataPtr->rawlen < MIN_RC6_MARKS + 3 && decodedIRData.rawDataPtr->rawlen > ((2 * RC6_BITS) + 3)) {
00355         IR_DEBUG_PRINT(F("RC6: "));
00356         IR_DEBUG_PRINT("Data length=");
00357         IR_DEBUG_PRINT(decodedIRData.rawDataPtr->rawlen);
00358         IR_DEBUG_PRINTLN(" is not between 15 and 45");
00359         return false;
00360     }
00361 
00362     // Check header "mark" and "space", this must be done for repeat and data
00363     if (!matchMark(decodedIRData.rawDataPtr->rawbuf[1], RC6_HEADER_MARK)
00364             || !matchSpace(decodedIRData.rawDataPtr->rawbuf[2], RC6_HEADER_SPACE)) {
00365         // no debug output, since this check is mainly to determine the received protocol
00366         IR_DEBUG_PRINT(F("RC6: "));
00367         IR_DEBUG_PRINTLN("Header mark or space length is wrong");
00368         return false;
00369     }
00370 
00371     // Set Biphase decoding start values
00372     initBiphaselevel(3, RC6_UNIT); // Skip gap-space and start-bit mark + space
00373 
00374 // Process first bit, which is known to be a 1 (mark->space)
00375     if (getBiphaselevel() != MARK) {
00376         IR_DEBUG_PRINT(F("RC6: "));
00377         IR_DEBUG_PRINTLN("first getBiphaselevel() is not MARK");
00378         return false;
00379     }
00380     if (getBiphaselevel() != SPACE) {
00381         IR_DEBUG_PRINT(F("RC6: "));
00382         IR_DEBUG_PRINTLN("second getBiphaselevel() is not SPACE");
00383         return false;
00384     }
00385 
00386     for (tBitIndex = 0; sBiphaseDecodeRawbuffOffset < decodedIRData.rawDataPtr->rawlen; tBitIndex++) {
00387         uint8_t tStartLevel; // start level of coded bit
00388         uint8_t tEndLevel;   // end level of coded bit
00389 
00390         tStartLevel = getBiphaselevel();
00391         if (tBitIndex == 3) {
00392             // Toggle bit is double wide; make sure second half is equal first half
00393             if (tStartLevel != getBiphaselevel()) {
00394                 IR_DEBUG_PRINT(F("RC6: "));
00395                 IR_DEBUG_PRINTLN(F("Toggle mark or space length is wrong"));
00396                 return false;
00397             }
00398         }
00399 
00400         tEndLevel = getBiphaselevel();
00401         if (tBitIndex == 3) {
00402             // Toggle bit is double wide; make sure second half matches
00403             if (tEndLevel != getBiphaselevel()) {
00404                 IR_DEBUG_PRINT(F("RC6: "));
00405                 IR_DEBUG_PRINTLN(F("Toggle mark or space length is wrong"));
00406                 return false;
00407             }
00408         }
00409 
00410         /*
00411          * Determine tDecodedRawData bit value by checking the transition type
00412          */
00413         if ((tStartLevel == MARK) && (tEndLevel == SPACE)) {
00414             // we have a mark to space transition here
00415             tDecodedRawData = (tDecodedRawData << 1) | 1;  // inverted compared to RC5
00416         } else if ((tStartLevel == SPACE) && (tEndLevel == MARK)) {
00417             // we have a space to mark transition here
00418             tDecodedRawData = (tDecodedRawData << 1) | 0;
00419         } else {
00420             IR_DEBUG_PRINT(F("RC6: "));
00421             IR_DEBUG_PRINTLN(F("Decode failed"));
00422             // we have no transition here or one level is -1 -> error
00423             return false;            // Error
00424         }
00425     }
00426 
00427 // Success
00428     decodedIRData.numberOfBits = tBitIndex;
00429 
00430     LongUnion tValue;
00431     tValue.ULong = tDecodedRawData;
00432     decodedIRData.decodedRawData = tDecodedRawData;
00433 
00434     if (tBitIndex < 36) {
00435         // RC6
00436         decodedIRData.flags = IRDATA_FLAGS_IS_MSB_FIRST;
00437         decodedIRData.command = tValue.UByte.LowByte;
00438         decodedIRData.address = tValue.UByte.MidLowByte;
00439         // Check for toggle flag
00440         if ((tValue.UByte.MidHighByte & 1) != 0) {
00441             decodedIRData.flags = IRDATA_TOGGLE_BIT_MASK | IRDATA_FLAGS_IS_MSB_FIRST;
00442         }
00443     } else {
00444         // RC6A
00445         decodedIRData.flags = IRDATA_FLAGS_IS_MSB_FIRST | IRDATA_FLAGS_EXTRA_INFO;
00446         if ((tValue.UByte.MidLowByte & 0x80) != 0) {
00447             decodedIRData.flags = IRDATA_TOGGLE_BIT_MASK | IRDATA_FLAGS_IS_MSB_FIRST | IRDATA_FLAGS_EXTRA_INFO;
00448         }
00449         tValue.UByte.MidLowByte &= 0x87F; // mask toggle bit
00450         decodedIRData.command = tValue.UByte.LowByte;
00451         decodedIRData.address = tValue.UByte.MidLowByte;
00452         // get extra info
00453         decodedIRData.extra = tValue.UWord.HighWord;
00454     }
00455 
00456     // check for repeat, do not check toggle bit yet
00457     if (decodedIRData.rawDataPtr->rawbuf[0] < ((RC6_REPEAT_SPACE + (RC6_REPEAT_SPACE / 4)) / MICROS_PER_TICK)) {
00458         decodedIRData.flags |= IRDATA_FLAGS_IS_REPEAT;
00459     }
00460 
00461     decodedIRData.protocol = RC6;
00462     return true;
00463 }
