00468 void IRsend::sendRC5(uint32_t data, uint8_t nbits) {
00469     // Set IR carrier frequency
00470     enableIROut(RC5_RC6_KHZ);
00471 
00472     // Start
00473     mark(RC5_UNIT);
00474     space(RC5_UNIT);
00475     mark(RC5_UNIT);
00476 
00477     // Data - Biphase code MSB first
00478     for (uint32_t mask = 1UL << (nbits - 1); mask; mask >>= 1) {
00479         if (data & mask) {
00480             space(RC5_UNIT); // 1 is space, then mark
00481             mark(RC5_UNIT);
00482         } else {
00483             mark(RC5_UNIT);
00484             space(RC5_UNIT);
00485         }
00486     }
00487 }
