00489 /*
00490  * Not longer required, use sendRC5 instead
00491  */
00492 void IRsend::sendRC5ext(uint8_t addr, uint8_t cmd, bool toggle) {
00493 // Set IR carrier frequency
00494     enableIROut(RC5_RC6_KHZ);
00495 
00496     uint8_t addressBits = 5;
00497     uint8_t commandBits = 7;
00498 //    unsigned long nbits = addressBits + commandBits;
00499 
00500 // Start
00501     mark(RC5_UNIT);
00502 
00503 // Bit #6 of the command part, but inverted!
00504     uint8_t cmdBit6 = (1UL << (commandBits - 1)) & cmd;
00505     if (cmdBit6) {
00506         // Inverted (1 -> 0 = mark-to-space)
00507         mark(RC5_UNIT);
00508         space(RC5_UNIT);
00509     } else {
00510         space(RC5_UNIT);
00511         mark(RC5_UNIT);
00512     }
00513     commandBits--;
00514 
00515 // Toggle bit
00516     static int toggleBit = 1;
00517     if (toggle) {
00518         if (toggleBit == 0) {
00519             toggleBit = 1;
00520         } else {
00521             toggleBit = 0;
00522         }
00523     }
00524     if (toggleBit) {
00525         space(RC5_UNIT);
00526         mark(RC5_UNIT);
00527     } else {
00528         mark(RC5_UNIT);
00529         space(RC5_UNIT);
00530     }
00531 
00532 // Address
00533     for (uint_fast8_t mask = 1UL << (addressBits - 1); mask; mask >>= 1) {
00534         if (addr & mask) {
00535             space(RC5_UNIT); // 1 is space, then mark
00536             mark(RC5_UNIT);
00537         } else {
00538             mark(RC5_UNIT);
00539             space(RC5_UNIT);
00540         }
00541     }
00542 
00543 // Command
00544     for (uint_fast8_t mask = 1UL << (commandBits - 1); mask; mask >>= 1) {
00545         if (cmd & mask) {
00546             space(RC5_UNIT); // 1 is space, then mark
00547             mark(RC5_UNIT);
00548         } else {
00549             mark(RC5_UNIT);
00550             space(RC5_UNIT);
00551         }
00552     }
00553 }
