00152 void IRrecv::enableIRIn() {
00153 
00154     noInterrupts();
00155 
00156     // Setup pulse clock TickCounterForISR interrupt
00157     timerConfigForReceive();
00158     TIMER_ENABLE_RECEIVE_INTR;  // Timer interrupt enable
00159     TIMER_RESET_INTR_PENDING;   // NOP for most platforms
00160 
00161     // Initialize state machine state
00162     resume();
00163     interrupts(); // after resume to avoid running through STOP state 1 time before switching to IDLE
00164 
00165     // Set pin modes
00166     pinMode(irparams.IRReceivePin, INPUT);
00167 }
00168 
00172 void IRrecv::disableIRIn() {
00173     TIMER_DISABLE_RECEIVE_INTR;
00174 }
