00199 void IRrecv::initDecodedIRData() {
00200 
00201     if (irparams.OverflowFlag) {
00202         // Copy overflow flag to decodedIRData.flags and reset it
00203         irparams.OverflowFlag = false;
00204         irparams.rawlen = 0; // otherwise we have OverflowFlag again at next ISR call
00205         decodedIRData.flags = IRDATA_FLAGS_WAS_OVERFLOW;
00206         IR_DEBUG_PRINTLN("Overflow happened");
00207 
00208     } else {
00209         decodedIRData.flags = IRDATA_FLAGS_EMPTY;
00210         // save last protocol, command and address for repeat handling (where the are copied back :-))
00211         lastDecodedProtocol = decodedIRData.protocol; // repeat patterns can be equal between protocols (e.g. NEC and LG), so we must keep the original one
00212         lastDecodedCommand = decodedIRData.command;
00213         lastDecodedAddress = decodedIRData.address;
00214 
00215     }
00216     decodedIRData.protocol = UNKNOWN;
00217     decodedIRData.command = 0;
00218     decodedIRData.address = 0;
00219     decodedIRData.decodedRawData = 0;
00220     decodedIRData.numberOfBits = 0;
00221 }
