00249 bool IRrecv::decode() {
00250     if (irparams.StateForISR != IR_REC_STATE_STOP) {
00251         return false;
00252     }
00253 
00254     initDecodedIRData(); // sets IRDATA_FLAGS_WAS_OVERFLOW
00255 
00256     if (decodedIRData.flags & IRDATA_FLAGS_WAS_OVERFLOW) {
00257         /*
00258          * Set OverflowFlag flag and return true here, to let the loop call resume or print raw data.
00259          */
00260         decodedIRData.protocol = UNKNOWN;
00261         return true;
00262     }
00263 
00264 #if defined(DECODE_NEC)
00265     IR_TRACE_PRINTLN("Attempting NEC decode");
00266     if (decodeNEC()) {
00267         return true;
00268     }
00269 #endif
00270 
00271 #if defined(DECODE_PANASONIC) || defined(DECODE_KASEIKYO)
00272     IR_TRACE_PRINTLN("Attempting Panasonic/Kaseikyo decode");
00273     if (decodeKaseikyo()) {
00274         return true;
00275     }
00276 #endif
