00349 #endif
00350 
00351 #if defined(DECODE_MAGIQUEST)
00352     IR_TRACE_PRINTLN("Attempting MagiQuest decode");
00353     if (decodeMagiQuest()) {
00354         return true;
00355     }
00356 #endif
00357 
00358     /*
00359      * Try the universal decoder for pulse width or pulse distance protocols
00360      */
00361 #if defined(DECODE_DISTANCE)
00362     IR_TRACE_PRINTLN("Attempting universal Distance decode");
00363     if (decodeDistance()) {
00364         return true;
00365     }
00366 #endif
00367 
00368     /*
00369      * Last resort is the universal hash decode which always return true
00370      */
00371 #if defined(DECODE_HASH)
00372     IR_TRACE_PRINTLN("Hash decode");
00373     // decodeHash returns a hash on any input.
00374     // Thus, it needs to be last in the list.
00375     // If you add any decodes, add them before this.
00376     if (decodeHash()) {
00377         return true;
00378     }
00379 #endif
00380 
00381     /*
00382      * Return true here, to let the loop decide to call resume or to print raw data.
00383      */
00384     return true;
00385 }
