00401 bool IRrecv::decodePulseWidthData(uint8_t aNumberOfBits, uint8_t aStartOffset, uint16_t aOneMarkMicros, uint16_t aZeroMarkMicros,
00402         uint16_t aBitSpaceMicros, bool aMSBfirst) {
00403 
00404     uint16_t *tRawBufPointer = &decodedIRData.rawDataPtr->rawbuf[aStartOffset];
00405     uint32_t tDecodedData = 0;
00406 
00407     if (aMSBfirst) {
00408         for (uint_fast8_t i = 0; i < aNumberOfBits; i++) {
00409             // Check for variable length mark indicating a 0 or 1
00410             if (matchMark(*tRawBufPointer, aOneMarkMicros)) {
00411                 tDecodedData = (tDecodedData << 1) | 1;
00412                 IR_TRACE_PRINT('1');
00413             } else if (matchMark(*tRawBufPointer, aZeroMarkMicros)) {
00414                 tDecodedData = (tDecodedData << 1) | 0;
00415                 IR_TRACE_PRINT('0');
00416             } else {
00417                 IR_DEBUG_PRINT(F("Mark="));
00418                 IR_DEBUG_PRINT(*tRawBufPointer * MICROS_PER_TICK);
00419                 IR_DEBUG_PRINT(F(" is not "));
00420                 IR_DEBUG_PRINT(aOneMarkMicros);
00421                 IR_DEBUG_PRINT(F(" or "));
00422                 IR_DEBUG_PRINT(aZeroMarkMicros);
00423                 IR_DEBUG_PRINT(' ');
00424                 return false;
00425             }
00426             tRawBufPointer++;
00427 
00428             if (tRawBufPointer < &decodedIRData.rawDataPtr->rawbuf[decodedIRData.rawDataPtr->rawlen]) {
00429                 // Assume that last space, which is not recorded, is correct, since we can not check it
00430                 // Check for constant length space
00431                 if (!matchSpace(*tRawBufPointer, aBitSpaceMicros)) {
00432                     IR_DEBUG_PRINT(F("Space="));
00433                     IR_DEBUG_PRINT(*tRawBufPointer * MICROS_PER_TICK);
00434                     IR_DEBUG_PRINT(F(" is not "));
00435                     IR_DEBUG_PRINT(aBitSpaceMicros);
00436                     IR_DEBUG_PRINT(' ');
00437                     return false;
00438                 }
00439                 tRawBufPointer++;
00440             }
00441         }
00442         IR_TRACE_PRINTLN("");
00443     } else {
00444         for (uint32_t tMask = 1UL; aNumberOfBits > 0; tMask <<= 1, aNumberOfBits--) {
00445 
00446             // Check for variable length mark indicating a 0 or 1
00447             if (matchMark(*tRawBufPointer, aOneMarkMicros)) {
00448                 tDecodedData |= tMask; // set the bit
00449                 IR_TRACE_PRINT('1');
00450             } else if (matchMark(*tRawBufPointer, aZeroMarkMicros)) {
00451                 // do not set the bit
00452                 IR_TRACE_PRINT('0');
00453             } else {
00454                 IR_DEBUG_PRINT(F("Mark="));
00455                 IR_DEBUG_PRINT(*tRawBufPointer * MICROS_PER_TICK);
00456                 IR_DEBUG_PRINT(F(" is not "));
00457                 IR_DEBUG_PRINT(aOneMarkMicros);
00458                 IR_DEBUG_PRINT(F(" or "));
00459                 IR_DEBUG_PRINT(aZeroMarkMicros);
00460                 IR_DEBUG_PRINT(' ');
00461                 return false;
00462             }
00463             tRawBufPointer++;
00464 
00465             if (tRawBufPointer < &decodedIRData.rawDataPtr->rawbuf[decodedIRData.rawDataPtr->rawlen]) {
00466                 // Assume that last space, which is not recorded, is correct, since we can not check it
00467                 // Check for constant length space
00468                 if (!matchSpace(*tRawBufPointer, aBitSpaceMicros)) {
00469                     IR_DEBUG_PRINT(F("Space="));
00470                     IR_DEBUG_PRINT(*tRawBufPointer * MICROS_PER_TICK);
00471                     IR_DEBUG_PRINT(F(" is not "));
00472                     IR_DEBUG_PRINT(aBitSpaceMicros);
00473                     IR_DEBUG_PRINT(' ');
00474                     return false;
00475                 }
00476                 tRawBufPointer++;
00477             }
00478         }
00479         IR_TRACE_PRINTLN("");
00480     }
00481     decodedIRData.decodedRawData = tDecodedData;
00482     return true;
00483 }
