00576  * Static variables for the getBiphaselevel function
00577  */
00578 uint8_t sBiphaseDecodeRawbuffOffset; // Index into raw timing array
00579 uint16_t sCurrentTimingIntervals; // Number of aBiphaseTimeUnit intervals of the current rawbuf[sBiphaseDecodeRawbuffOffset] timing.
00580 uint8_t sUsedTimingIntervals;       // Number of already used intervals of sCurrentTimingIntervals.
00581 uint16_t sBiphaseTimeUnit;
00582 
00583 void IRrecv::initBiphaselevel(uint8_t aRCDecodeRawbuffOffset, uint16_t aBiphaseTimeUnit) {
00584     sBiphaseDecodeRawbuffOffset = aRCDecodeRawbuffOffset;
00585     sBiphaseTimeUnit = aBiphaseTimeUnit;
00586     sUsedTimingIntervals = 0;
00587 }
00588 
00605 uint8_t IRrecv::getBiphaselevel() {
00606     uint8_t tLevelOfCurrentInterval; // 0 (SPACE) or 1 (MARK)
00607 
00608     if (sBiphaseDecodeRawbuffOffset >= decodedIRData.rawDataPtr->rawlen) {
00609         return SPACE;  // After end of recorded buffer, assume space.
00610     }
00611 
00612     tLevelOfCurrentInterval = (sBiphaseDecodeRawbuffOffset) & 1; // on odd rawbuf offsets we have mark timings
00613 
00614     /*
00615      * Setup data if sUsedTimingIntervals is 0
00616      */
00617     if (sUsedTimingIntervals == 0) {
00618         uint16_t tCurrentTimingWith = decodedIRData.rawDataPtr->rawbuf[sBiphaseDecodeRawbuffOffset];
00619         uint16_t tMarkExcessCorrection = (tLevelOfCurrentInterval == MARK) ? MARK_EXCESS_MICROS : -MARK_EXCESS_MICROS;
00620 
00621         if (matchTicks(tCurrentTimingWith, (sBiphaseTimeUnit) + tMarkExcessCorrection)) {
00622             sCurrentTimingIntervals = 1;
00623         } else if (matchTicks(tCurrentTimingWith, (2 * sBiphaseTimeUnit) + tMarkExcessCorrection)) {
00624             sCurrentTimingIntervals = 2;
00625         } else if (matchTicks(tCurrentTimingWith, (3 * sBiphaseTimeUnit) + tMarkExcessCorrection)) {
00626             sCurrentTimingIntervals = 3;
00627         } else {
00628             return -1;
00629         }
00630     }
00631 
00632     // We use another interval from tCurrentTimingIntervals
00633     sUsedTimingIntervals++;
00634 
00635     // keep track of current timing offset
00636     if (sUsedTimingIntervals >= sCurrentTimingIntervals) {
00637         // we have used all intervals of current timing, switch to next timing value
00638         sUsedTimingIntervals = 0;
00639         sBiphaseDecodeRawbuffOffset++;
00640     }
00641 
00642     IR_TRACE_PRINTLN(tLevelOfCurrentInterval);
00643 
00644     return tLevelOfCurrentInterval;
00645 }
