00656 uint8_t IRrecv::compare(unsigned int oldval, unsigned int newval) {
00657     if (newval * 10 < oldval * 8) {
00658         return 0;
00659     }
00660     if (oldval * 10 < newval * 8) {
00661         return 2;
00662     }
00663     return 1;
00664 }
