00666 #define FNV_PRIME_32 16777619   
00667 #define FNV_BASIS_32 2166136261 
00668 
00687 bool IRrecv::decodeHash() {
00688     unsigned long hash = FNV_BASIS_32; // the result is the same no matter if we use a long or unsigned long variable
00689 
00690 // Require at least 6 samples to prevent triggering on noise
00691     if (decodedIRData.rawDataPtr->rawlen < 6) {
00692         return false;
00693     }
00694 #if RAW_BUFFER_LENGTH <= 254        // saves around 75 bytes program space and speeds up ISR
00695     uint8_t i;
00696 #else
00697     uint16_t i;
00698 #endif
00699     for (i = 1; (i + 2) < decodedIRData.rawDataPtr->rawlen; i++) {
00700         uint8_t value = compare(decodedIRData.rawDataPtr->rawbuf[i], decodedIRData.rawDataPtr->rawbuf[i + 2]);
00701         // Add value into the hash
00702         hash = (hash * FNV_PRIME_32) ^ value;
00703     }
00704 
00705     decodedIRData.decodedRawData = hash;
00706     decodedIRData.numberOfBits = 32;
00707     decodedIRData.protocol = UNKNOWN;
00708 
00709     return true;
00710 }
00711 
00712 bool IRrecv::decodeHashOld(decode_results *aResults) {
00713     unsigned long hash = FNV_BASIS_32;
00714 
00715 // Require at least 6 samples to prevent triggering on noise
00716     if (aResults->rawlen < 6) {
00717         return false;
00718     }
00719 
00720     for (unsigned int i = 1; (i + 2) < aResults->rawlen; i++) {
00721         uint8_t value = compare(aResults->rawbuf[i], aResults->rawbuf[i + 2]);
00722         // Add value into the hash
00723         hash = (hash * FNV_PRIME_32) ^ value;
00724     }
00725 
00726     aResults->value = hash;
00727     aResults->bits = 32;
00728     aResults->decode_type = UNKNOWN;
00729     decodedIRData.protocol = UNKNOWN;
00730 
00731     return true;
00732 }
