00742 bool matchTicks(uint16_t aMeasuredTicks, uint16_t aMatchValueMicros) {
00743 #ifdef TRACE
00744     Serial.print(F("Testing: "));
00745     Serial.print(TICKS_LOW(aMatchValueMicros), DEC);
00746     Serial.print(F(" <= "));
00747     Serial.print(aMeasuredTicks, DEC);
00748     Serial.print(F(" <= "));
00749     Serial.print(TICKS_HIGH(aMatchValueMicros), DEC);
00750 #endif
00751     bool passed = ((aMeasuredTicks >= TICKS_LOW(aMatchValueMicros)) && (aMeasuredTicks <= TICKS_HIGH(aMatchValueMicros)));
00752 #ifdef TRACE
00753     if (passed) {
00754         Serial.println(F("?; passed"));
00755     } else {
00756         Serial.println(F("?; FAILED"));
00757     }
00758 #endif
00759     return passed;
00760 }
