00769 bool matchMark(uint16_t aMeasuredTicks, uint16_t aMatchValueMicros) {
00770 #ifdef TRACE
00771     Serial.print(F("Testing mark (actual vs desired): "));
00772     Serial.print(aMeasuredTicks * MICROS_PER_TICK, DEC);
00773     Serial.print(F("us vs "));
00774     Serial.print(aMatchValueMicros, DEC);
00775     Serial.print(F("us: "));
00776     Serial.print(TICKS_LOW(aMatchValueMicros + MARK_EXCESS_MICROS) * MICROS_PER_TICK, DEC);
00777     Serial.print(F(" <= "));
00778     Serial.print(aMeasuredTicks * MICROS_PER_TICK, DEC);
00779     Serial.print(F(" <= "));
00780     Serial.print(TICKS_HIGH(aMatchValueMicros + MARK_EXCESS_MICROS) * MICROS_PER_TICK, DEC);
00781 #endif
00782     // compensate for marks exceeded by demodulator hardware
00783     bool passed = ((aMeasuredTicks >= TICKS_LOW(aMatchValueMicros + MARK_EXCESS_MICROS))
00784             && (aMeasuredTicks <= TICKS_HIGH(aMatchValueMicros + MARK_EXCESS_MICROS)));
00785 #ifdef TRACE
00786     if (passed) {
00787         Serial.println(F("?; passed"));
00788     } else {
00789         Serial.println(F("?; FAILED"));
00790     }
00791 #endif
00792     return passed;
00793 }
00794 
00795 bool MATCH_MARK(uint16_t measured_ticks, uint16_t desired_us) {
00796     return matchMark(measured_ticks, desired_us);
00797 }
