00839 /*
00840  * Check if protocol is not detected and detected space between two transmissions
00841  * is smaller than known value for protocols (Sony with around 24 ms)
00842  */
00843 void CheckForRecordGapsMicros(Print *aSerial, IRData *aIRDataPtr) {
00844     /*
00845      * Check if protocol is not detected and detected space between two transmissions
00846      * is smaller than known value for protocols (Sony with around 24 ms)
00847      */
00848     if (aIRDataPtr->protocol <= PULSE_WIDTH && aIRDataPtr->rawDataPtr->rawbuf[0] < RECORD_GAP_MICROS_WARNING_THRESHOLD) {
00849         aSerial->println();
00850         aSerial->print(F("Space between two detected transmission is greater than "));
00851         aSerial->print(RECORD_GAP_MICROS);
00852         aSerial->print(F(" but smaller than the minimal gap of "));
00853         aSerial->print(RECORD_GAP_MICROS_WARNING_THRESHOLD);
00854         aSerial->println(F(" known for a protocol."));
00855         aSerial->println(F("If you get unexpected results, try to increase the RECORD_GAP_MICROS in IRremote.h."));
00856         aSerial->println();
00857     }
00858 }
