00924 void printIRResultShort(Print *aSerial, IRData *aIRDataPtr, uint16_t aLeadingSpaceTicks) {
00925     aSerial->print(F("Protocol="));
00926     aSerial->print(getProtocolString(aIRDataPtr->protocol));
00927     if (aIRDataPtr->protocol == UNKNOWN) {
00928 #if defined(DECODE_HASH)
00929         aSerial->print(F(" Hash=0x"));
00930         aSerial->print(aIRDataPtr->decodedRawData, HEX);
00931 #endif
00932         aSerial->print(' ');
00933         aSerial->print((aIRDataPtr->rawDataPtr->rawlen + 1) / 2, DEC);
00934         aSerial->println(F(" bits received"));
00935     } else {
00936         /*
00937          * New decoders have address and command
00938          */
00939         aSerial->print(F(" Address=0x"));
00940         aSerial->print(aIRDataPtr->address, HEX);
00941 
00942         aSerial->print(F(" Command=0x"));
00943         aSerial->print(aIRDataPtr->command, HEX);
00944 
00945         if (aIRDataPtr->flags & IRDATA_FLAGS_EXTRA_INFO) {
00946             aSerial->print(F(" Extra=0x"));
00947             aSerial->print(aIRDataPtr->extra, HEX);
00948         }
00949 
00950         if (aIRDataPtr->flags & IRDATA_FLAGS_PARITY_FAILED) {
00951             aSerial->print(F(" Parity fail"));
00952         }
00953 
00954         if (aIRDataPtr->flags & IRDATA_TOGGLE_BIT_MASK) {
00955             aSerial->print(F(" Toggle=1"));
00956         }
00957 
00958         if (aIRDataPtr->flags & (IRDATA_FLAGS_IS_AUTO_REPEAT | IRDATA_FLAGS_IS_REPEAT)) {
00959             aSerial->print(' ');
00960             if (aIRDataPtr->flags & IRDATA_FLAGS_IS_AUTO_REPEAT) {
00961                 aSerial->print(F("Auto-"));
00962             }
00963             aSerial->print(F("Repeat"));
00964             if (aLeadingSpaceTicks != 0) {
00965                 aSerial->print(F(" gap="));
00966                 aSerial->print((uint32_t) aLeadingSpaceTicks * MICROS_PER_TICK);
00967                 aSerial->print(F("us"));
00968             }
00969         }
00970 
00971         /*
00972          * Print raw data
00973          */
00974         if (!(aIRDataPtr->flags & IRDATA_FLAGS_IS_REPEAT) || aIRDataPtr->decodedRawData != 0) {
00975             aSerial->print(F(" Raw-Data=0x"));
00976             aSerial->print(aIRDataPtr->decodedRawData, HEX);
00977 
00978             /*
00979              * Print number of bits processed
00980              */
00981             aSerial->print(' ');
00982             aSerial->print(aIRDataPtr->numberOfBits, DEC);
00983             aSerial->print(F(" bits"));
00984 
00985             if (aIRDataPtr->flags & IRDATA_FLAGS_IS_MSB_FIRST) {
00986                 aSerial->println(F(" MSB first"));
00987             } else {
00988                 aSerial->println(F(" LSB first"));
00989             }
00990 
00991         } else {
00992             aSerial->println();
00993         }
00994 
00995         CheckForRecordGapsMicros(aSerial, aIRDataPtr);
00996     }
00997 }
