01050 void IRrecv::printIRResultRawFormatted(Print *aSerial, bool aOutputMicrosecondsInsteadOfTicks) {
01051     // Print Raw data
01052     aSerial->print(F("rawData["));
01053     aSerial->print(decodedIRData.rawDataPtr->rawlen, DEC);
01054     aSerial->println(F("]: "));
01055 
01056     uint32_t tDurationMicros;
01057 
01058     /*
01059      * Print initial gap
01060      */
01061     if (aOutputMicrosecondsInsteadOfTicks) {
01062         tDurationMicros = (uint32_t) decodedIRData.rawDataPtr->rawbuf[0] * MICROS_PER_TICK;
01063     } else {
01064         tDurationMicros = decodedIRData.rawDataPtr->rawbuf[0];
01065     }
01066     aSerial->print(F("     -"));
01067     aSerial->println(tDurationMicros, DEC);
01068 #if RAW_BUFFER_LENGTH <= 254        // saves around 75 bytes program space and speeds up ISR
01069     uint8_t i;
01070 #else
01071     uint16_t i;
01072 #endif
01073     for (i = 1; i < decodedIRData.rawDataPtr->rawlen; i++) {
01074         if (aOutputMicrosecondsInsteadOfTicks) {
01075             tDurationMicros = decodedIRData.rawDataPtr->rawbuf[i] * MICROS_PER_TICK;
01076         } else {
01077             tDurationMicros = decodedIRData.rawDataPtr->rawbuf[i];
01078         }
01079         if (!(i & 1)) {  // even
01080             aSerial->print('-');
01081         } else {  // odd
01082             aSerial->print(F("     +"));
01083         }
01084         if (tDurationMicros < 1000) {
01085             aSerial->print(' ');
01086         }
01087         if (tDurationMicros < 100) {
01088             aSerial->print(' ');
01089         }
01090         if (tDurationMicros < 10) {
01091             aSerial->print(' ');
01092         }
01093         aSerial->print(tDurationMicros, DEC);
01094 
01095         if ((i & 1) && (i + 1) < decodedIRData.rawDataPtr->rawlen) {
01096             aSerial->print(','); //',' not required for last one
01097         }
01098 
01099         if (!(i % 8)) {
01100             aSerial->println("");
01101         }
01102     }
01103     aSerial->println("");                    // Newline
01104 }
