01117 void IRrecv::compensateAndPrintIRResultAsCArray(Print *aSerial, bool aOutputMicrosecondsInsteadOfTicks) {
01118 // Start declaration
01119     if (aOutputMicrosecondsInsteadOfTicks) {
01120         aSerial->print(F("uint16_t "));         // variable type
01121         aSerial->print(F("rawData["));          // array name
01122     } else {
01123         aSerial->print(F("uint8_t "));          // variable type
01124         aSerial->print(F("rawTicks["));         // array name
01125     }
01126 
01127     aSerial->print(decodedIRData.rawDataPtr->rawlen - 1, DEC);    // array size
01128     aSerial->print(F("] = {"));    // Start declaration
01129 
01130 // Dump data
01131 #if RAW_BUFFER_LENGTH <= 254        // saves around 75 bytes program space and speeds up ISR
01132     uint8_t i;
01133 #else
01134     uint16_t i;
01135 #endif
01136     for (i = 1; i < decodedIRData.rawDataPtr->rawlen; i++) {
01137         uint32_t tDuration = decodedIRData.rawDataPtr->rawbuf[i] * MICROS_PER_TICK;
01138 
01139         if (i & 1) {
01140             // Mark
01141             tDuration -= MARK_EXCESS_MICROS;
01142         } else {
01143             tDuration += MARK_EXCESS_MICROS;
01144         }
01145 
01146         if (aOutputMicrosecondsInsteadOfTicks) {
01147             aSerial->print(tDuration);
01148         } else {
01149             uint16_t tTicks = (tDuration + (MICROS_PER_TICK / 2)) / MICROS_PER_TICK;
01150             tTicks = (tTicks > 0xFF) ? 0xFF : tTicks; // safety net
01151             aSerial->print(tTicks);
01152         }
01153         if (i + 1 < decodedIRData.rawDataPtr->rawlen)
01154             aSerial->print(',');                // ',' not required on last one
01155         if (!(i & 1))
01156             aSerial->print(' ');
01157     }
01158 
01159 // End declaration
01160     aSerial->print(F("};"));                //
01161 
01162 // Comment
01163     aSerial->print(F("  // "));
01164     printIRResultShort(aSerial);
01165 
01166 // Newline
01167     aSerial->println("");
01168 }
