01179 void IRrecv::compensateAndStoreIRResultInArray(uint8_t *aArrayPtr) {
01180 
01181 // Store data, skip leading space#
01182 #if RAW_BUFFER_LENGTH <= 254        // saves around 75 bytes program space and speeds up ISR
01183     uint8_t i;
01184 #else
01185     uint16_t i;
01186 #endif
01187     for (i = 1; i < decodedIRData.rawDataPtr->rawlen; i++) {
01188         uint32_t tDuration = decodedIRData.rawDataPtr->rawbuf[i] * MICROS_PER_TICK;
01189         if (i & 1) {
01190             // Mark
01191             tDuration -= MARK_EXCESS_MICROS;
01192         } else {
01193             tDuration += MARK_EXCESS_MICROS;
01194         }
01195 
01196         uint16_t tTicks = (tDuration + (MICROS_PER_TICK / 2)) / MICROS_PER_TICK;
01197         *aArrayPtr = (tTicks > 0xFF) ? 0xFF : tTicks; // safety net
01198         aArrayPtr++;
01199     }
01200 }
