01314 
01332 //#define _IR_MEASURE_TIMING
01333 //#define _IR_TIMING_TEST_PIN 7 // do not forget to execute: "pinMode(_IR_TIMING_TEST_PIN, OUTPUT);" if activated by line above
01334 #if defined(_IR_MEASURE_TIMING) && defined(_IR_TIMING_TEST_PIN)
01335 #include "digitalWriteFast.h"
01336 #endif
01337 #if defined(TIMER_INTR_NAME)
01338 ISR (TIMER_INTR_NAME) // for ISR definitions
01339 #else
01340 ISR () // for functions definitions which are called by separate (board specific) ISR
01341 #endif
01342 {
01343 #if defined(_IR_MEASURE_TIMING) && defined(_IR_TIMING_TEST_PIN)
01344     digitalWriteFast(_IR_TIMING_TEST_PIN, HIGH); // 2 clock cycles
01345 #endif
01346 // 7 - 8.5 us for ISR body (without pushes and pops) for ATmega328 @16MHz
01347 
01348     TIMER_RESET_INTR_PENDING;// reset TickCounterForISR interrupt flag if required (currently only for Teensy and ATmega4809)
01349 
01350 // Read if IR Receiver -> SPACE [xmt LED off] or a MARK [xmt LED on]
01351 #if defined(__AVR__)
01352     uint8_t tIRInputLevel = *irparams.IRReceivePinPortInputRegister & irparams.IRReceivePinMask;
01353 #else
01354     uint8_t tIRInputLevel = (uint8_t) digitalRead(irparams.IRReceivePin);
01355 #endif
01356 
01357     /*
01358      * Increase TickCounter and clip it at maximum 0xFFFF / 3.2 seconds at 50 us ticks
01359      */
01360     if (irparams.TickCounterForISR < 0xFFFF) {
01361         irparams.TickCounterForISR++;  // One more 50uS tick
01362     }
01363 
01364     /*
01365      * Due to a ESP32 compiler bug https://github.com/espressif/esp-idf/issues/1552 no switch statements are possible for ESP32
01366      * So we change the code to if / else if
01367      */
01368 //    switch (irparams.StateForISR) {
01369 //......................................................................
01370     if (irparams.StateForISR == IR_REC_STATE_IDLE) { // In the middle of a gap or just resumed (and maybe in the middle of a transmission
01371         if (tIRInputLevel == INPUT_MARK) {
01372             // check if we did not start in the middle of a transmission by checking the minimum length of leading space
01373             if (irparams.TickCounterForISR > RECORD_GAP_TICKS) {
01374                 // Gap just ended; Record gap duration + start recording transmission
01375                 // Initialize all state machine variables
01376 #if defined(_IR_MEASURE_TIMING) && defined(_IR_TIMING_TEST_PIN)
01377 //                digitalWriteFast(_IR_TIMING_TEST_PIN, HIGH); // 2 clock cycles
01378 #endif
01379                 irparams.OverflowFlag = false;
01380                 irparams.rawbuf[0] = irparams.TickCounterForISR;
01381                 irparams.rawlen = 1;
01382                 irparams.StateForISR = IR_REC_STATE_MARK;
01383             } // otherwise stay in idle state
01384             irparams.TickCounterForISR = 0;// reset counter in both cases
01385         }
01386 
01387     } else if (irparams.StateForISR == IR_REC_STATE_MARK) {  // Timing mark
01388         if (tIRInputLevel != INPUT_MARK) {   // Mark ended; Record time
01389 #if defined(_IR_MEASURE_TIMING) && defined(_IR_TIMING_TEST_PIN)
01390 //            digitalWriteFast(_IR_TIMING_TEST_PIN, HIGH); // 2 clock cycles
01391 #endif
01392             irparams.rawbuf[irparams.rawlen++] = irparams.TickCounterForISR;
01393             irparams.StateForISR = IR_REC_STATE_SPACE;
01394             irparams.TickCounterForISR = 0;
01395         }
01396 
01397     } else if (irparams.StateForISR == IR_REC_STATE_SPACE) {  // Timing space
01398         if (tIRInputLevel == INPUT_MARK) {  // Space just ended; Record time
01399             if (irparams.rawlen >= RAW_BUFFER_LENGTH) {
01400                 // Flag up a read OverflowFlag; Stop the state machine
01401                 irparams.OverflowFlag = true;
01402                 irparams.StateForISR = IR_REC_STATE_STOP;
01403             } else {
01404 #if defined(_IR_MEASURE_TIMING) && defined(_IR_TIMING_TEST_PIN)
01405 //                digitalWriteFast(_IR_TIMING_TEST_PIN, HIGH); // 2 clock cycles
01406 #endif
01407                 irparams.rawbuf[irparams.rawlen++] = irparams.TickCounterForISR;
01408                 irparams.StateForISR = IR_REC_STATE_MARK;
01409             }
01410             irparams.TickCounterForISR = 0;
01411 
01412         } else if (irparams.TickCounterForISR > RECORD_GAP_TICKS) {
01413             /*
01414              * Current code is ready for processing!
01415              * We received a long space, which indicates gap between codes.
01416              * Switch to IR_REC_STATE_STOP
01417              * Don't reset TickCounterForISR; keep counting width of next leading space
01418              */
01419             irparams.StateForISR = IR_REC_STATE_STOP;
01420         }
01421     } else if (irparams.StateForISR == IR_REC_STATE_STOP) {
01422         /*
01423          * Complete command received
01424          * stay here until resume() is called, which switches state to IR_REC_STATE_IDLE
01425          */
01426 #if defined(_IR_MEASURE_TIMING) && defined(_IR_TIMING_TEST_PIN)
01427 //        digitalWriteFast(_IR_TIMING_TEST_PIN, HIGH); // 2 clock cycles
01428 #endif
01429         if (tIRInputLevel == INPUT_MARK) {
01430             // Reset gap TickCounterForISR, to prepare for detection if we are in the middle of a transmission after call of resume()
01431             irparams.TickCounterForISR = 0;
01432         }
01433     }
01434 
01435 #if !defined(NO_LED_FEEDBACK_CODE)
01436     if (FeedbackLEDControl.LedFeedbackEnabled) {
01437         setFeedbackLED(tIRInputLevel == INPUT_MARK);
01438     }
01439 #endif
01440 
01441 #ifdef _IR_MEASURE_TIMING
01442     digitalWriteFast(_IR_TIMING_TEST_PIN, LOW); // 2 clock cycles
01443 #endif
01444 }
01445 
01446 
01453 bool IRrecv::decode(decode_results *aResults) {
01454     static bool sDeprecationMessageSent = false;
01455 
01456     if (irparams.StateForISR != IR_REC_STATE_STOP) {
01457         return false;
01458     }
01459 
01460     if (!sDeprecationMessageSent) {
01461 #if !(defined(__AVR_ATtiny25__) || defined(__AVR_ATtiny45__) || defined(__AVR_ATtiny85__) || defined(__AVR_ATtiny87__) || defined(__AVR_ATtiny167__))
01462         Serial.println(
01463                 "The function decode(&results)) is deprecated and may not work as expected! Just use decode() without a parameter and IrReceiver.decodedIRData.<fieldname> .");
01464 #endif
01465         sDeprecationMessageSent = true;
01466     }
01467 
01468     // copy for usage by legacy programs
01469     aResults->rawbuf = irparams.rawbuf;
01470     aResults->rawlen = irparams.rawlen;
01471     if (irparams.OverflowFlag) {
01472         // Copy overflow flag to decodedIRData.flags
01473         irparams.OverflowFlag = false;
01474         irparams.rawlen = 0; // otherwise we have OverflowFlag again at next ISR call
01475         IR_DEBUG_PRINTLN("Overflow happened");
01476     }
01477     aResults->overflow = irparams.OverflowFlag;
01478     aResults->value = 0;
01479 
01480     decodedIRData.flags = IRDATA_FLAGS_IS_MSB_FIRST; // for print
01481 
01482 #if defined(DECODE_NEC)
01483     IR_DEBUG_PRINTLN("Attempting old NEC decode");
01484     if (decodeNECMSB(aResults)) {
01485         return true ;
01486     }
01487 #endif
01488 
01489 #if defined(DECODE_SONY)
01490     IR_DEBUG_PRINTLN("Attempting old Sony decode");
01491     if (decodeSonyMSB(aResults))  {
01492         return true ;
01493     }
01494 #endif
01495 
01496 //#if defined(DECODE_MITSUBISHI)
01497 //    IR_DEBUG_PRINTLN("Attempting Mitsubishi decode");
01498 //    if (decodeMitsubishi(results))  return true ;
01499 //#endif
01500 
01501 #if defined(DECODE_RC5)
01502     IR_DEBUG_PRINTLN("Attempting RC5 decode");
01503     if (decodeRC5()) {
01504         aResults->bits = decodedIRData.numberOfBits;
01505         aResults->value = decodedIRData.decodedRawData;
01506         aResults->decode_type = RC5;
01507 
01508         return true ;
01509     }
01510 #endif
01511 
01512 #if defined(DECODE_RC6)
01513     IR_DEBUG_PRINTLN("Attempting RC6 decode");
01514     if (decodeRC6())  {
01515         aResults->bits = decodedIRData.numberOfBits;
01516         aResults->value = decodedIRData.decodedRawData;
01517         aResults->decode_type = RC6;
01518         return true ;
01519     }
01520 #endif
01521 
01522 #if defined( DECODE_PANASONIC)
01523     IR_DEBUG_PRINTLN("Attempting old Panasonic decode");
01524     if (decodePanasonicMSB(aResults)) {
01525         return true ;
01526     }
01527 #endif
01528 
01529 #if defined(DECODE_LG)
01530     IR_DEBUG_PRINTLN("Attempting old LG decode");
01531     if (decodeLGMSB(aResults)) { return true ;}
01532 #endif
01533 
01534 #if defined(DECODE_JVC)
01535     IR_DEBUG_PRINTLN("Attempting old JVC decode");
01536     if (decodeJVCMSB(aResults)) {
01537         return true ;
01538     }
01539 #endif
01540 
01541 #if defined(DECODE_SAMSUNG)
01542     IR_DEBUG_PRINTLN("Attempting old SAMSUNG decode");
01543     if (decodeSAMSUNG(aResults)) {
01544         return true ;
01545     }
01546 #endif
01547 
01548 //#if defined(DECODE_WHYNTER)
01549 //    IR_DEBUG_PRINTLN("Attempting Whynter decode");
01550 //    if (decodeWhynter(results))  return true ;
01551 //#endif
01552 
01553 #if defined(DECODE_DENON)
01554     IR_DEBUG_PRINTLN("Attempting old Denon decode");
01555     if (decodeDenonOld(aResults)) {
01556         return true ;
01557     }
01558 #endif
01559 
01560 //#if defined(DECODE_LEGO_PF)
01561 //    IR_DEBUG_PRINTLN("Attempting Lego Power Functions");
01562 //    if (decodeLegoPowerFunctions(results))  return true ;
01563 //#endif
01564 
01565     // decodeHash returns a hash on any input.
01566     // Thus, it needs to be last in the list.
01567     // If you add any decodes, add them before this.
01568     if (decodeHashOld(aResults)) {
01569         return true;
01570     }
01571     // Throw away and start over
01572     resume();
01573     return false;
01574 }
