00096 //+=============================================================================
00097 void IRsend::sendDenon(uint8_t aAddress, uint8_t aCommand, uint_fast8_t aNumberOfRepeats, bool aSendSharp) {
00098     // Set IR carrier frequency
00099     enableIROut(DENON_KHZ); // 38 kHz
00100 
00101     // Shift command and add frame marker
00102     uint16_t tCommand = aCommand << DENON_FRAME_BITS; // the lowest bits are 00 for Denon and 10 for Sharp
00103     if (aSendSharp) {
00104         tCommand |= 0x02;
00105     }
00106     uint16_t tData = tCommand | ((uint16_t) aAddress << (DENON_COMMAND_BITS + DENON_FRAME_BITS));
00107     uint16_t tInvertedData = ((~tCommand) & 0x3FF) | (uint16_t) aAddress << (DENON_COMMAND_BITS + DENON_FRAME_BITS);
00108 
00109     uint_fast8_t tNumberOfCommands = aNumberOfRepeats + 1;
00110     while (tNumberOfCommands > 0) {
00111 
00112         // Data
00113         sendPulseDistanceWidthData(DENON_BIT_MARK, DENON_ONE_SPACE, DENON_BIT_MARK, DENON_ZERO_SPACE, tData, DENON_BITS, PROTOCOL_IS_MSB_FIRST,
00114         SEND_STOP_BIT);
00115 
00116         // Inverted autorepeat frame
00117         delay(DENON_AUTO_REPEAT_SPACE / MICROS_IN_ONE_MILLI);
00118         sendPulseDistanceWidthData(DENON_BIT_MARK, DENON_ONE_SPACE, DENON_BIT_MARK, DENON_ZERO_SPACE, tInvertedData, DENON_BITS,
00119         PROTOCOL_IS_MSB_FIRST, SEND_STOP_BIT);
00120 
00121         tNumberOfCommands--;
00122         // skip last delay!
00123         if (tNumberOfCommands > 0) {
00124             // send repeated command with a fixed space gap
00125             delay( DENON_AUTO_REPEAT_SPACE / MICROS_IN_ONE_MILLI);
00126         }
00127     }
00128 }
