00135 //+=============================================================================
00136 bool IRrecv::decodeDenon() {
00137 
00138     // we have no start bit, so check for the exact amount of data bits
00139     // Check we have the right amount of data (32). The + 2 is for initial gap + stop bit mark
00140     if (decodedIRData.rawDataPtr->rawlen != (2 * DENON_BITS) + 2) {
00141         IR_DEBUG_PRINT(F("Denon: "));
00142         IR_DEBUG_PRINT("Data length=");
00143         IR_DEBUG_PRINT(decodedIRData.rawDataPtr->rawlen);
00144         IR_DEBUG_PRINTLN(" is not 32");
00145         return false;
00146     }
00147 
00148     // Read the bits in
00149     if (!decodePulseDistanceData(DENON_BITS, 1, DENON_BIT_MARK, DENON_ONE_SPACE, DENON_ZERO_SPACE, PROTOCOL_IS_MSB_FIRST)) {
00150         IR_DEBUG_PRINT("Denon: ");
00151         IR_DEBUG_PRINTLN("Decode failed");
00152         return false;
00153     }
00154 
00155     // Check for stop mark
00156     if (!matchMark(decodedIRData.rawDataPtr->rawbuf[(2 * DENON_BITS) + 1], DENON_HEADER_MARK)) {
00157         IR_DEBUG_PRINT("Denon: ");
00158         IR_DEBUG_PRINTLN(F("Stop bit mark length is wrong"));
00159         return false;
00160     }
00161 
00162     // Success
00163     decodedIRData.flags = IRDATA_FLAGS_IS_MSB_FIRST;
00164     uint8_t tFrameBits = decodedIRData.decodedRawData & 0x03;
00165     decodedIRData.command = decodedIRData.decodedRawData >> DENON_FRAME_BITS;
00166     decodedIRData.address = decodedIRData.command >> DENON_COMMAND_BITS;
00167     uint8_t tCommand = decodedIRData.command & 0xFF;
00168     decodedIRData.command = tCommand;
00169 
00170     // check for autorepeated inverted command
00171     if (decodedIRData.rawDataPtr->rawbuf[0] < ((DENON_AUTO_REPEAT_SPACE + (DENON_AUTO_REPEAT_SPACE / 4)) / MICROS_PER_TICK)) {
00172         repeatCount++;
00173         if (tFrameBits == 0x3 || tFrameBits == 0x1) {
00174             // We are in the auto repeated frame with the inverted command
00175             decodedIRData.flags = IRDATA_FLAGS_IS_AUTO_REPEAT | IRDATA_FLAGS_IS_MSB_FIRST;
00176             // Check parity of consecutive received commands. There is no parity in one data set.
00177             uint8_t tLastCommand = lastDecodedCommand;
00178             if (tLastCommand != (uint8_t) (~tCommand)) {
00179                 decodedIRData.flags |= IRDATA_FLAGS_PARITY_FAILED;
00180             }
00181             // always take non inverted command
00182             decodedIRData.command = tLastCommand;
00183         }
00184         if (repeatCount > 1) {
00185             decodedIRData.flags |= IRDATA_FLAGS_IS_REPEAT;
00186         }
00187     } else {
00188         repeatCount = 0;
00189     }
00190 
00191     decodedIRData.numberOfBits = DENON_BITS;
00192     if (tFrameBits == 1 || tFrameBits == 2) {
00193         decodedIRData.protocol = SHARP;
00194     } else {
00195         decodedIRData.protocol = DENON;
00196     }
00197     return true;
00198 }
