00200 bool IRrecv::decodeDenonOld(decode_results *aResults) {
00201 
00202     // Check we have the right amount of data
00203     if (decodedIRData.rawDataPtr->rawlen != 1 + 2 + (2 * DENON_BITS) + 1) {
00204         return false;
00205     }
00206 
00207     // Check initial Mark+Space match
00208     if (!matchMark(aResults->rawbuf[1], DENON_HEADER_MARK)) {
00209         return false;
00210     }
00211 
00212     if (!matchSpace(aResults->rawbuf[2], DENON_HEADER_SPACE)) {
00213         return false;
00214     }
00215 
00216     // Read the bits in
00217     if (!decodePulseDistanceData(DENON_BITS, 3, DENON_BIT_MARK, DENON_ONE_SPACE, DENON_ZERO_SPACE, PROTOCOL_IS_MSB_FIRST)) {
00218         return false;
00219     }
00220 
00221     // Success
00222     aResults->value = decodedIRData.decodedRawData;
00223     aResults->bits = DENON_BITS;
00224     aResults->decode_type = DENON;
00225     decodedIRData.protocol = DENON;
00226     return true;
00227 }
