00229 void IRsend::sendDenon(unsigned long data, int nbits) {
00230     // Set IR carrier frequency
00231     enableIROut(DENON_KHZ);
00232 #if !(defined(__AVR_ATtiny25__) || defined(__AVR_ATtiny45__) || defined(__AVR_ATtiny85__) || defined(__AVR_ATtiny87__) || defined(__AVR_ATtiny167__))
00233     Serial.println(
00234             "The function sendDenon(data, nbits) is deprecated and may not work as expected! Use sendDenonRaw(data, NumberOfRepeats) or better sendDenon(Address, Command, NumberOfRepeats).");
00235 #endif
00236 
00237     // Header
00238     mark(DENON_HEADER_MARK);
00239     space(DENON_HEADER_SPACE);
00240 
00241     // Data
00242     sendPulseDistanceWidthData(DENON_BIT_MARK, DENON_ONE_SPACE, DENON_BIT_MARK, DENON_ZERO_SPACE, data, nbits, PROTOCOL_IS_MSB_FIRST,
00243     SEND_STOP_BIT);
00244 
00245 }
