00255 void IRsend::sendRaw(const uint16_t aBufferWithMicroseconds[], uint_fast8_t aLengthOfBuffer, uint_fast8_t aIRFrequencyKilohertz) {
00256 // Set IR carrier frequency
00257     enableIROut(aIRFrequencyKilohertz);
00258 
00259     /*
00260      * Raw data starts with a mark.
00261      */
00262     for (uint_fast8_t i = 0; i < aLengthOfBuffer; i++) {
00263         if (i & 1) {
00264             // Odd
00265             space(aBufferWithMicroseconds[i]);
00266         } else {
00267             mark(aBufferWithMicroseconds[i]);
00268         }
00269     }
00270 
00271 //    ledOff();  // Always end with the LED off
00272 }
