00347 void IRsend::sendPulseDistanceWidthData(unsigned int aOneMarkMicros, unsigned int aOneSpaceMicros, unsigned int aZeroMarkMicros,
00348         unsigned int aZeroSpaceMicros, uint32_t aData, uint8_t aNumberOfBits, bool aMSBfirst, bool aSendStopBit) {
00349 
00350     if (aMSBfirst) {  // Send the MSB first.
00351         // send data from MSB to LSB until mask bit is shifted out
00352         for (uint32_t tMask = 1UL << (aNumberOfBits - 1); tMask; tMask >>= 1) {
00353             if (aData & tMask) {
00354                 IR_TRACE_PRINT('1');
00355                 mark(aOneMarkMicros);
00356                 space(aOneSpaceMicros);
00357             } else {
00358                 IR_TRACE_PRINT('0');
00359                 mark(aZeroMarkMicros);
00360                 space(aZeroSpaceMicros);
00361             }
00362         }
00363     } else {  // Send the Least Significant Bit (LSB) first / MSB last.
00364         for (uint_fast8_t bit = 0; bit < aNumberOfBits; bit++, aData >>= 1)
00365             if (aData & 1) {  // Send a 1
00366                 IR_TRACE_PRINT('1');
00367                 mark(aOneMarkMicros);
00368                 space(aOneSpaceMicros);
00369             } else {  // Send a 0
00370                 IR_TRACE_PRINT('0');
00371                 mark(aZeroMarkMicros);
00372                 space(aZeroSpaceMicros);
00373             }
00374     }
00375     if (aSendStopBit) {
00376         IR_TRACE_PRINT('S');
00377         mark(aZeroMarkMicros); // seems like this is used for stop bits
00378     }
00379     IR_TRACE_PRINTLN("");
00380 }
00381 
00382 /*
00383  * Sends Biphase data MSB first
00384  * Always send start bit, do not send the trailing space of the start bit
00385  * 0 -> mark+space
00386  * 1 -> space+mark
00387  * The output always ends with a space
00388  */
00389 void IRsend::sendBiphaseData(unsigned int aBiphaseTimeUnit, uint32_t aData, uint_fast8_t aNumberOfBits) {
00390 
00391 // do not send the trailing space of the start bit
00392     mark(aBiphaseTimeUnit);
00393 
00394     IR_TRACE_PRINT('S');
00395     uint8_t tLastBitValue = 1; // Start bit is a 1
00396 
00397 // Data - Biphase code MSB first
00398     for (uint32_t tMask = 1UL << (aNumberOfBits - 1); tMask; tMask >>= 1) {
00399         if (aData & tMask) {
00400             IR_TRACE_PRINT('1');
00401             space(aBiphaseTimeUnit);
00402             mark(aBiphaseTimeUnit);
00403             tLastBitValue = 1;
00404 
00405         } else {
00406             IR_TRACE_PRINT('0');
00407 #if defined(SEND_PWM_BY_TIMER) || defined(USE_NO_SEND_PWM)
00408             if (tLastBitValue) {
00409                 // Extend the current mark in order to generate a continuous signal without short breaks
00410                 delayMicroseconds(aBiphaseTimeUnit);
00411             } else {
00412                 mark(aBiphaseTimeUnit);
00413             }
00414 #else
00415             (void) tLastBitValue; // to avoid compiler warnings
00416             mark(aBiphaseTimeUnit); // can not eventually delay here, we must call mark to generate the signal
00417 #endif
00418             space(aBiphaseTimeUnit);
00419             tLastBitValue = 0;
00420         }
00421     }
00422     IR_TRACE_PRINTLN("");
00423 }
