00431 void IRsend::mark(unsigned int aMarkMicros) {
00432 
00433 #if defined(SEND_PWM_BY_TIMER)
00434 #  if !defined(NO_LED_FEEDBACK_CODE)
00435     setFeedbackLED(true);
00436 #  endif
00437     ENABLE_SEND_PWM_BY_TIMER; // Enable timer or ledcWrite() generated PWM output
00438     customDelayMicroseconds(aMarkMicros);
00439     IRLedOff();
00440 #  if !defined(NO_LED_FEEDBACK_CODE)
00441     setFeedbackLED(false);
00442 #  endif
00443 
00444 #elif defined(USE_NO_SEND_PWM)
00445 #  if !defined(NO_LED_FEEDBACK_CODE)
00446     setFeedbackLED(true);
00447 #  endif
00448 #  if defined(USE_OPEN_DRAIN_OUTPUT_FOR_SEND_PIN) && !defined(OUTPUT_OPEN_DRAIN)
00449     pinModeFast(sendPin, OUTPUT); // active state for mimicking open drain
00450 #  else
00451     digitalWriteFast(sendPin, LOW); // Set output to active low.
00452 #  endif
00453 
00454     customDelayMicroseconds(aMarkMicros);
00455     IRLedOff();
00456 #  if !defined(NO_LED_FEEDBACK_CODE)
00457     setFeedbackLED(false);
00458 #  endif
00459 
00460 #else
00461     unsigned long startMicros = micros();
00462     unsigned long nextPeriodEnding = startMicros;
00463     unsigned long tMicros;
00464 #  if !defined(NO_LED_FEEDBACK_CODE)
00465     bool FeedbackLedIsActive = false;
00466 #  endif
00467 
00468     do {
00469 //        digitalToggleFast(_IR_TIMING_TEST_PIN);
00470         // Output the PWM pulse
00471         noInterrupts(); // do not let interrupts extend the short on period
00472 #  if defined(USE_OPEN_DRAIN_OUTPUT_FOR_SEND_PIN)
00473 #    if defined(OUTPUT_OPEN_DRAIN)
00474         digitalWriteFast(sendPin, LOW); // active state for open drain
00475 #    else
00476         pinModeFast(sendPin, OUTPUT); // active state for mimicking open drain
00477 #    endif
00478 #  else
00479 #    if defined(IR_SEND_PIN)
00480         digitalWriteFast(IR_SEND_PIN, HIGH); // 3.5 us from FeedbackLed on to pin setting. 5.7 us from call of mark() to pin setting incl. setting of feedback pin.
00481 #    else
00482         digitalWrite(sendPin, HIGH); // 4.3 us from do{ to pin setting
00483 #    endif
00484 #  endif
00485         delayMicroseconds(periodOnTimeMicros); // this is normally implemented by a blocking wait
00486 
00487         // Output the PWM pause
00488 #  if defined(USE_OPEN_DRAIN_OUTPUT_FOR_SEND_PIN) && !defined(OUTPUT_OPEN_DRAIN)
00489 #    if defined(OUTPUT_OPEN_DRAIN)
00490         digitalWriteFast(sendPin, HIGH); // Set output to inactive high.
00491 #    else
00492         pinModeFast(sendPin, INPUT); // inactive state to mimic open drain
00493 #    endif
00494 
00495 #  else
00496         digitalWriteFast(sendPin, LOW);
00497 #  endif
00498         interrupts(); // Enable interrupts -to keep micros correct- for the longer off period 3.4 us until receive ISR is active (for 7 us + pop's)
00499 
00500 #  if !defined(NO_LED_FEEDBACK_CODE)
00501         /*
00502          * Delayed call of setFeedbackLED() to get better timing
00503          */
00504         if (!FeedbackLedIsActive) {
00505             FeedbackLedIsActive = true;
00506             setFeedbackLED(true);
00507         }
00508 #  endif
00509         /*
00510          * Pause timing
00511          */
00512         nextPeriodEnding += periodTimeMicros;
00513         do {
00514             tMicros = micros(); // we have only 4 us resolution for AVR @16MHz
00515             // check for aMarkMicros to be gone
00516             unsigned int tDeltaMicros = tMicros - startMicros;
00517 #if defined(__AVR__)
00518 //            tDeltaMicros += (160 / CLOCKS_PER_MICRO); // adding this once increases program size !
00519 #  if !defined(NO_LED_FEEDBACK_CODE)
00520             if (tDeltaMicros >= aMarkMicros - (30 + (112 / CLOCKS_PER_MICRO))) { // 30 to be constant. Using periodTimeMicros increases program size too much.
00521             // reset feedback led in the last pause before end
00522                 setFeedbackLED(false);
00523             }
00524 #  endif
00525             if (tDeltaMicros >= aMarkMicros - (112 / CLOCKS_PER_MICRO)) { // To compensate for call duration - 112 is an empirical value
00526 #else
00527             if (tDeltaMicros >= aMarkMicros) {
00528 #endif
00529                 return;
00530             }
00531 //            digitalToggleFast(_IR_TIMING_TEST_PIN); // 3.0 us per call @16MHz
00532         } while (tMicros < nextPeriodEnding);  // 3.4 us @16MHz
00533     } while (true);
00534 #  endif
00535 }
