00542 void IRsend::IRLedOff() {
00543 #if defined(SEND_PWM_BY_TIMER)
00544     DISABLE_SEND_PWM_BY_TIMER; // Disable PWM output
00545 #elif defined(USE_NO_SEND_PWM)
00546 #  if defined(USE_OPEN_DRAIN_OUTPUT_FOR_SEND_PIN) && !defined(OUTPUT_OPEN_DRAIN)
00547     digitalWriteFast(sendPin, LOW); // prepare for all next active states.
00548     pinModeFast(sendPin, INPUT); // inactive state for open drain
00549 #  else
00550     digitalWriteFast(sendPin, HIGH); // Set output to inactive high.
00551 #  endif
00552 #else
00553 #  if defined(USE_OPEN_DRAIN_OUTPUT_FOR_SEND_PIN)
00554 #    if defined(OUTPUT_OPEN_DRAIN)
00555     digitalWriteFast(sendPin, HIGH); // Set output to inactive high.
00556 #    else
00557     pinModeFast(sendPin, INPUT); // inactive state to mimic open drain
00558 #    endif
00559 #  else
00560     digitalWriteFast(sendPin, LOW);
00561 #  endif
00562 #endif
00563 #if !defined(NO_LED_FEEDBACK_CODE)
00564     setFeedbackLED(false);
00565 #endif
00566 }
